SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_pagto_caixa_240_v80 ( nr_seq_envio_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* PADRAO CNAB 240 VERSAO 080 */

ds_conteudo_w		varchar(240);
cd_estabelecimento_w	smallint;
nr_seq_apres_w		bigint	:= 0;

/* header de arquivo */

cd_agencia_estab_w	varchar(6);
cd_cgc_estab_w		varchar(14);
cd_convenio_banco_w	varchar(6);
dt_geracao_w		varchar(14);
nm_empresa_w		varchar(30);
nr_conta_estab_w	varchar(12);
nr_remessa_w		bigint;
ie_digito_estab_w	varchar(1);
ds_banco_w		varchar(30);
cd_banco_cobr_w		smallint;

/* header de lote - DOC */

nr_lote_servico_w	bigint;
ie_forma_lanc_w		varchar(2);
ie_tipo_pagamento_w	varchar(3);
ie_finalidade_w		varchar(2);
ds_endereco_w		varchar(30);
nr_endereco_w		varchar(5);
ds_complemento_w	varchar(15);
ds_municipio_w		varchar(20);
nr_cep_w		varchar(8);
sg_estado_w		varchar(15);

c01 CURSOR FOR
SELECT	distinct
	CASE WHEN b.ie_tipo_pagamento='CC' THEN '01' WHEN b.ie_tipo_pagamento='DOC' THEN '03' WHEN b.ie_tipo_pagamento='TED' THEN '41' WHEN b.ie_tipo_pagamento='OP' THEN '30'  ELSE '03' END ,
	b.ie_tipo_pagamento,
	CASE WHEN b.ie_tipo_pagamento='CC' THEN '01'  ELSE '07' END
from	titulo_pagar_escrit b,
	banco_escritural a
where
b.ie_tipo_pagamento	in ('DOC','CC','OP','TED')
and not exists (select	1
	from	darf_titulo_pagar x
	where	x.nr_titulo	= b.nr_titulo)
and not exists (select	1
	from	gps_titulo x
	where	x.nr_titulo	= b.nr_titulo)
and	a.nr_sequencia		= b.nr_seq_escrit
and	a.nr_sequencia		= nr_seq_envio_p;

/* detalhe - DOC */

nr_sequencia_w		bigint;
cd_camara_compensacao_w	varchar(3);
cd_banco_w		smallint;
cd_agencia_bancaria_w	varchar(6);
nr_conta_w		varchar(20);
nm_pessoa_w		varchar(30);
nr_titulo_w		bigint;
dt_remessa_retorno_w	timestamp;
vl_escritural_w		double precision;
vl_acrescimo_w		double precision;
vl_desconto_w		double precision;
vl_despesa_w		double precision;
ie_tipo_inscricao_w	varchar(1);
nr_inscricao_w		varchar(14);
dt_vencimento_w		timestamp;
vl_juros_w		double precision;
vl_multa_w		double precision;
cd_agencia_conta_w	varchar(20);
ie_digito_conta_w	varchar(1);
ds_endereco_det_w	varchar(30);
nr_endereco_det_w	varchar(5);
ds_complemento_det_w	varchar(15);
ds_municipio_det_w	varchar(20);
nr_cep_det_w		varchar(8);
sg_estado_det_w		varchar(15);
ds_bairro_det_w		varchar(15);
cd_camara_w		varchar(3);
qt_favorecido_w		bigint;

c02 CURSOR FOR
SELECT	b.cd_banco,
	lpad(substr(b.cd_agencia_bancaria,1,5) || substr(b.ie_digito_agencia,1,1),6,'0'),
	rpad(UPPER(elimina_caractere_esp_asc(substr(obter_nome_pf_pj(c.cd_pessoa_fisica,c.cd_cgc),1,30),'S','N')),30,' '),
	c.nr_titulo,
	b.vl_escritural,
	b.vl_acrescimo,
	b.vl_desconto,
	b.vl_despesa,
	lpad(coalesce(d.nr_cpf,c.cd_cgc),14,'0'),
	b.nr_conta,
	substr(b.ie_digito_conta,1,1),
	c.dt_vencimento_atual,
	b.vl_juros,
	b.vl_multa,
	CASE WHEN coalesce(c.cd_pessoa_fisica::text, '') = '' THEN '2'  ELSE '1' END  ie_tipo_inscricao,
	rpad(coalesce(UPPER(elimina_caractere_esp_asc(substr(obter_dados_pf_pj(c.cd_pessoa_fisica,c.cd_cgc,'R'),1,30),'S','N')),' '),30,' '),	
	lpad(coalesce(substr(obter_dados_pf_pj(c.cd_pessoa_fisica,c.cd_cgc,'NR'),1,5),'0'),5,'0'),
	rpad(coalesce(UPPER(elimina_caractere_esp_asc(substr(obter_dados_pf_pj(c.cd_pessoa_fisica,c.cd_cgc,'CO'),1,15),'S','N')),' '),15,' '),	
	rpad(coalesce(UPPER(elimina_caractere_esp_asc(substr(obter_dados_pf_pj(c.cd_pessoa_fisica,c.cd_cgc,'CI'),1,20),'S','N')),' '),20,' '),	
	lpad(coalesce(substr(obter_dados_pf_pj(c.cd_pessoa_fisica,c.cd_cgc,'CEP'),1,8),'0'),8,'0'),
	rpad(coalesce(substr(obter_dados_pf_pj(c.cd_pessoa_fisica,c.cd_cgc,'UF'),1,2),' '),2,' '),
	rpad(coalesce(UPPER(elimina_caractere_esp_asc(substr(obter_dados_pf_pj(c.cd_pessoa_fisica,c.cd_cgc,'B'),1,15),'S','N')),' '),15,' '),	
	CASE WHEN b.ie_tipo_pagamento='DOC' THEN '700' WHEN b.ie_tipo_pagamento='TED' THEN '018'  ELSE '000' END  cd_camara
FROM titulo_pagar_escrit b, banco_escritural a, titulo_pagar c
LEFT OUTER JOIN pessoa_fisica d ON (c.cd_pessoa_fisica = d.cd_pessoa_fisica)
WHERE b.nr_titulo		= c.nr_titulo and b.ie_tipo_pagamento	= ie_tipo_pagamento_w and a.nr_sequencia		= b.nr_seq_escrit and a.nr_sequencia		= nr_seq_envio_p;

/* trailer lote - DOC */

vl_total_w		double precision;

/* header de lote - BLQ */

c03 CURSOR FOR
SELECT	distinct
	CASE WHEN(select	count(*)		from	gps_titulo x		where	x.nr_titulo	= b.nr_titulo)=0 THEN 		CASE WHEN(select	count(*)			from	darf_titulo_pagar x			where	x.nr_titulo	= b.nr_titulo)=0 THEN 			CASE WHEN 	b.ie_tipo_pagamento='PC' THEN '11'  ELSE CASE WHEN 	CASE WHEN coalesce(d.cd_banco_externo::text, '') = '' THEN b.cd_banco  ELSE somente_numero(d.cd_banco_externo) END =cd_banco_cobr_w THEN '30'  ELSE CASE WHEN(select	count(*)						from	banco x						where	CASE WHEN coalesce(x.cd_banco_externo::text, '') = '' THEN x.cd_banco  ELSE somente_numero(x.cd_banco_externo) END 	= somente_numero(substr(c.nr_bloqueto,1,3)))=0 THEN '11'  ELSE '31' END  END  END   ELSE CASE WHEN 	coalesce(c.nr_bloqueto::text, '') = '' THEN '16'  ELSE '11' END  END   ELSE CASE WHEN coalesce(c.nr_bloqueto::text, '') = '' THEN '17'  ELSE '11' END  END  ie_forma_lanc,
	b.ie_tipo_pagamento
FROM titulo_pagar c, banco_escritural a, titulo_pagar_escrit b
LEFT OUTER JOIN banco d ON (b.cd_banco = d.cd_banco)
WHERE b.nr_titulo		= c.nr_titulo and (b.ie_tipo_pagamento in ('BLQ','PC') or
	exists (select	1
	from	darf_titulo_pagar x
	where	x.nr_titulo	= b.nr_titulo) or
	exists (select	1
	from	gps_titulo x
	where	x.nr_titulo	= b.nr_titulo)) and a.nr_sequencia		= b.nr_seq_escrit and a.nr_sequencia		= nr_seq_envio_p order by	1;

/* detalhe - BLQ */

nr_bloqueto_w		varchar(44);
nr_nosso_numero_w	varchar(20);
ie_tipo_servico_w	varchar(2);
ie_converte_bloq_w	varchar(1);
dt_venc_bloqueto_w	timestamp;
ie_tipo_identificacao_w	varchar(2);
qt_banco_w		bigint;
dt_apuracao_w		timestamp;
cd_darf_w		varchar(6);
dt_vencimento_darf_w timestamp;
nr_referencia_w		varchar(17);
cd_pagamento_w		varchar(6);
dt_referencia_w		varchar(6);
vl_inss_w		double precision;
vl_outras_entidades_w	double precision;
nr_versao_w		varchar(3);

c04 CURSOR FOR
SELECT	rpad(elimina_caractere_especial(substr(obter_nome_pf_pj(c.cd_pessoa_fisica,c.cd_cgc),1,30)),30,' '),
	coalesce(c.nr_titulo,0),
	b.vl_escritural,
	b.vl_acrescimo,
	b.vl_desconto,
	b.vl_despesa,
	c.dt_vencimento_atual,
	b.vl_juros,
	b.vl_multa,
	rpad(substr(CASE WHEN coalesce(ie_converte_bloq_w,'N')='S' THEN converte_codigo_bloqueto('Lido_Barra',c.nr_bloqueto)  ELSE c.nr_bloqueto END ,1,44),44,' '),
	rpad(substr(coalesce(c.nr_nosso_numero,' '),1,20),20,' '),
	CASE WHEN coalesce(c.cd_cgc::text, '') = '' THEN '02'  ELSE '01' END  ie_tipo_identificacao,
	lpad(coalesce(c.cd_cgc,d.nr_cpf),14,'0') nr_inscricao
FROM banco_escritural a, titulo_pagar c
LEFT OUTER JOIN pessoa_fisica d ON (c.cd_pessoa_fisica = d.cd_pessoa_fisica)
, titulo_pagar_escrit b
LEFT OUTER JOIN banco e ON (b.cd_banco = e.cd_banco)
WHERE b.nr_titulo		= c.nr_titulo and CASE WHEN(select	count(*)		from	gps_titulo x		where	x.nr_titulo	= b.nr_titulo)=0 THEN 		CASE WHEN(select	count(*)			from	darf_titulo_pagar x			where	x.nr_titulo	= b.nr_titulo)=0 THEN 			CASE WHEN 	b.ie_tipo_pagamento='PC' THEN '11'  ELSE CASE WHEN 	CASE WHEN coalesce(e.cd_banco_externo::text, '') = '' THEN b.cd_banco  ELSE somente_numero(e.cd_banco_externo) END =cd_banco_cobr_w THEN '30'  ELSE CASE WHEN(select	count(*)						from	banco x						where	CASE WHEN coalesce(x.cd_banco_externo::text, '') = '' THEN x.cd_banco  ELSE somente_numero(x.cd_banco_externo) END 	= somente_numero(substr(c.nr_bloqueto,1,3)))=0 THEN '11'  ELSE '31' END  END  END   ELSE CASE WHEN 	coalesce(c.nr_bloqueto::text, '') = '' THEN '16'  ELSE '11' END  END   ELSE CASE WHEN coalesce(c.nr_bloqueto::text, '') = '' THEN '17'  ELSE '11' END  END 	= ie_forma_lanc_w  and b.ie_tipo_pagamento	= ie_tipo_pagamento_w and a.nr_sequencia		= b.nr_seq_escrit and a.nr_sequencia		= nr_seq_envio_p;

/* trailer do arquivo */

qt_registro_w		bigint;


BEGIN

delete	from w_envio_banco
where	nm_usuario	= nm_usuario_p;

/* header de arquivo */

nr_seq_apres_w		:= coalesce(nr_seq_apres_w,0) + 1;

select	lpad(coalesce(b.cd_cgc,'0'),14,'0'),
	rpad(substr(coalesce(c.cd_convenio_banco,' '),1,6),6,' '),
	lpad(substr(coalesce(c.cd_agencia_bancaria,'0'),1,5) || substr(coalesce(d.ie_digito,' '),1,1),6,'0'),
	lpad(somente_numero(substr(coalesce(c.cd_conta,'0'),1,12)),12,'0'),
	rpad(UPPER(elimina_caractere_esp_asc(substr(coalesce(obter_nome_estabelecimento(b.cd_estabelecimento),' '),1,30),'S','N')),30,' '),	
	to_char(clock_timestamp(),'ddmmyyyyhhmiss'),
	coalesce(a.nr_remessa,a.nr_sequencia),
	b.cd_estabelecimento,
	a.dt_remessa_retorno,
	rpad(coalesce(UPPER(elimina_caractere_esp_asc(substr(obter_dados_pf_pj(null,b.cd_cgc,'R'),1,30),'S','N')),' '),30,' '),	
	lpad(coalesce(substr(obter_dados_pf_pj(null,b.cd_cgc,'NR'),1,5),'0'),5,'0'),
	rpad(coalesce(UPPER(elimina_caractere_esp_asc(substr(obter_dados_pf_pj(null,b.cd_cgc,'CO'),1,15),'S','N')),' '),15,' '),	
	rpad(coalesce(UPPER(elimina_caractere_esp_asc(substr(obter_dados_pf_pj(null,b.cd_cgc,'CI'),1,20),'S','N')),' '),20,' '),
	lpad(coalesce(substr(obter_dados_pf_pj(null,b.cd_cgc,'CEP'),1,8),'0'),8,'0'),
	rpad(coalesce(substr(obter_dados_pf_pj(null,b.cd_cgc,'UF'),1,2),' '),2,' '),
	substr(coalesce(c.ie_digito_conta,'0'),1,1),
	coalesce(c.cd_banco,0),
	substr(e.ds_banco,1,30) ds_banco
into STRICT	cd_cgc_estab_w,
	cd_convenio_banco_w,
	cd_agencia_estab_w,
	nr_conta_estab_w,
	nm_empresa_w,
	dt_geracao_w,
	nr_remessa_w,
	cd_estabelecimento_w,
	dt_remessa_retorno_w,
	ds_endereco_w,
	nr_endereco_w,
	ds_complemento_w,
	ds_municipio_w,
	nr_cep_w,
	sg_estado_w,
	ie_digito_estab_w,
	cd_banco_cobr_w,
	ds_banco_w
from	banco e,
	agencia_bancaria d,
	banco_estabelecimento c,
	estabelecimento b,
	banco_escritural a
where	c.cd_banco		= e.cd_banco
and	c.cd_banco		= d.cd_banco
and	c.cd_agencia_bancaria	= d.cd_agencia_bancaria
and	a.nr_seq_conta_banco	= c.nr_sequencia
and	a.cd_estabelecimento	= b.cd_estabelecimento
and	a.nr_sequencia		= nr_seq_envio_p;

ie_converte_bloq_w := obter_param_usuario(857, 36, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_converte_bloq_w);

ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||
			'0000' ||
			'0' ||
			rpad(' ',9,' ') ||
			'2' ||
			cd_cgc_estab_w ||
			cd_convenio_banco_w ||
			'01' ||
			'P' ||
			' ' ||
			rpad(' ',3,' ') ||
			lpad('0',4,'0') ||
			rpad(' ',3,' ') ||
			cd_agencia_estab_w ||
			nr_conta_estab_w ||
			ie_digito_estab_w ||
			' ' ||
			nm_empresa_w ||
			rpad(upper(ds_banco_w),30,' ') ||
			rpad(' ',10,' ') ||
			'1' ||
			dt_geracao_w ||
			lpad(nr_remessa_w,6,'0') ||
			'080' ||
			'01600' ||
			rpad(' ',20,' ') ||
			rpad(' ',20,' ') ||
			rpad(' ',11,' ') ||
			rpad(' ',3,' ') ||
			'000' ||
			rpad(' ',2,' ') ||
			rpad(' ',10,' ');

insert	into w_envio_banco(cd_estabelecimento,
	ds_conteudo,
	dt_atualizacao,
	nm_usuario,
	nr_seq_apres,
	nr_seq_apres_2,
	nr_sequencia)
values (cd_estabelecimento_w,
	ds_conteudo_w,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_apres_w,
	nr_seq_apres_w,
	nextval('w_envio_banco_seq'));

open	c01;
loop
fetch	c01 into
	ie_forma_lanc_w,
	ie_tipo_pagamento_w,
	ie_finalidade_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	/* header de lote */

	nr_lote_servico_w	:= coalesce(nr_lote_servico_w,0) + 1;
	nr_seq_apres_w		:= coalesce(nr_seq_apres_w,0) + 1;
	nr_sequencia_w		:= 0;
	vl_total_w		:= 0;

	ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||
				lpad(nr_lote_servico_w,4,'0') ||
				'1' ||
				'C' ||
				'20' ||
				ie_forma_lanc_w ||
				'041' ||
				' ' ||
				'2' ||
				cd_cgc_estab_w ||
				cd_convenio_banco_w ||
				'01' ||
				'0001' ||
				'01' ||
				rpad(' ',6,' ') ||
				cd_agencia_estab_w ||
				nr_conta_estab_w ||
				ie_digito_estab_w ||
				' ' ||
				nm_empresa_w ||
				rpad(' ',40,' ') ||
				ds_endereco_w ||
				nr_endereco_w ||
				ds_complemento_w ||
				ds_municipio_w ||
				nr_cep_w ||
				substr(sg_estado_w,1,2) ||
				rpad(' ',18,' ');

	insert	into w_envio_banco(cd_estabelecimento,
		ds_conteudo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres,
		nr_seq_apres_2,
		nr_sequencia)
	values (cd_estabelecimento_w,
		ds_conteudo_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		nr_seq_apres_w,
		nextval('w_envio_banco_seq'));

	open	c02;
	loop
	fetch	c02 into
		cd_banco_w,
		cd_agencia_bancaria_w,
		nm_pessoa_w,
		nr_titulo_w,
		vl_escritural_w,
		vl_acrescimo_w,
		vl_desconto_w,
		vl_despesa_w,
		nr_inscricao_w,
		nr_conta_w,
		ie_digito_conta_w,
		dt_vencimento_w,
		vl_juros_w,
		vl_multa_w,
		ie_tipo_inscricao_w,
		ds_endereco_det_w,
		nr_endereco_det_w,
		ds_complemento_det_w,
		ds_municipio_det_w,
		nr_cep_det_w,
		sg_estado_det_w,
		ds_bairro_det_w,
		cd_camara_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		select	count(*)
		into STRICT	qt_favorecido_w
		from	titulo_pagar_favorecido a
		where	a.nr_titulo	= nr_titulo_w;

		if (coalesce(qt_favorecido_w,0)	> 0) then

			select	somente_numero(substr(obter_conta_pessoa(coalesce(a.cd_cgc,a.cd_pessoa_fisica),CASE WHEN coalesce(a.cd_cgc::text, '') = '' THEN 'F'  ELSE 'J' END ,'B'),1,3)) cd_banco,
				lpad(substr(obter_conta_pessoa(coalesce(a.cd_cgc,a.cd_pessoa_fisica),CASE WHEN coalesce(a.cd_cgc::text, '') = '' THEN 'F'  ELSE 'J' END ,'A'),1,5) ||
				substr(obter_conta_pessoa(coalesce(a.cd_cgc,a.cd_pessoa_fisica),CASE WHEN coalesce(a.cd_cgc::text, '') = '' THEN 'F'  ELSE 'J' END ,'DA'),1,1),6,'0') cd_agencia_bancaria,
				substr(obter_conta_pessoa(coalesce(a.cd_cgc,a.cd_pessoa_fisica),CASE WHEN coalesce(a.cd_cgc::text, '') = '' THEN 'F'  ELSE 'J' END ,'C'),1,255) nr_conta,
				coalesce(substr(obter_conta_pessoa(coalesce(a.cd_cgc,a.cd_pessoa_fisica),CASE WHEN coalesce(a.cd_cgc::text, '') = '' THEN 'F'  ELSE 'J' END ,'DC'),1,1),'0') ie_digito_conta
			into STRICT	cd_banco_w,
				cd_agencia_bancaria_w,
				nr_conta_w,
				ie_digito_conta_w
			from	titulo_pagar_favorecido a
			where	a.nr_titulo	= nr_titulo_w;

		end if;

		/* segmento A */

		nr_sequencia_w	:= coalesce(nr_sequencia_w,0) + 1;
		nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;
		qt_registro_w	:= coalesce(qt_registro_w,0) + 1;
		vl_total_w	:= coalesce(vl_total_w,0) + (coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0));

		if (ie_tipo_pagamento_w <> 'DOC') then
			ie_finalidade_w := '00';
    end if;

		ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||
					lpad(nr_lote_servico_w,4,'0') ||
					'3' ||
					lpad(nr_sequencia_w,5,'0') ||
					'A' ||
					'0' ||
					'00' ||
					cd_camara_w ||
					lpad(cd_banco_w,3,'0') ||
					cd_agencia_bancaria_w ||
					lpad(substr(nr_conta_w,1,12),12,'0') ||
					ie_digito_conta_w ||
					' ' ||
					nm_pessoa_w ||
					lpad(substr(nr_titulo_w,1,6),6,'0') ||
					rpad(' ',13,' ') ||
					'1' ||
					to_char(dt_remessa_retorno_w,'ddmmyyyy') ||
					'BRL' ||
					'000000000000000' ||
					lpad(somente_numero(to_char(coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0),'9999999999990.00')),15,'0') ||
					lpad('0',9,'0') ||
					rpad(' ',3,' ') ||
					'01' ||
					'N' ||
					'1' ||
					to_char(dt_vencimento_w,'dd') ||
					'00' ||
					'00000000' ||
					'000000000000000' ||
					rpad(' ',40,' ') ||
					ie_finalidade_w ||
					rpad(' ',10,' ') ||
					'0' ||
					rpad(' ',10,' ');

		insert	into w_envio_banco(cd_estabelecimento,
			ds_conteudo,
			dt_atualizacao,
			nm_usuario,
			nr_seq_apres,
			nr_seq_apres_2,
			nr_sequencia)
		values (cd_estabelecimento_w,
			ds_conteudo_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_apres_w,
			nr_seq_apres_w,
			nextval('w_envio_banco_seq'));

		/* segmento B */

		nr_sequencia_w	:= coalesce(nr_sequencia_w,0) + 1;
		nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;
		qt_registro_w	:= coalesce(qt_registro_w,0) + 1;

		ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||
					lpad(nr_lote_servico_w,4,'0') ||
					'3' ||
					lpad(nr_sequencia_w,5,'0') ||
					'B' ||
					rpad(' ',3,' ') ||
					ie_tipo_inscricao_w ||
					nr_inscricao_w ||
					ds_endereco_det_w ||
					nr_endereco_det_w ||
					ds_complemento_det_w ||
					ds_bairro_det_w ||
					ds_municipio_det_w ||
					nr_cep_det_w ||
					substr(sg_estado_det_w,1,2) ||
					to_char(dt_vencimento_w,'ddmmyyyy') ||
					lpad('0',15,'0') ||
					'000000000000000' ||
					lpad(somente_numero(to_char(coalesce(vl_desconto_w,0),'9999999999990.00')),15,'0') ||
					lpad(somente_numero(to_char(coalesce(vl_juros_w,0),'9999999999990.00')),15,'0') ||
					lpad(somente_numero(to_char(coalesce(vl_multa_w,0),'9999999999990.00')),15,'0') ||
					rpad(' ',15,' ') ||
					rpad(' ',15,' ');

		insert	into w_envio_banco(cd_estabelecimento,
			ds_conteudo,
			dt_atualizacao,
			nm_usuario,
			nr_seq_apres,
			nr_seq_apres_2,
			nr_sequencia)
		values (cd_estabelecimento_w,
			ds_conteudo_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_apres_w,
			nr_seq_apres_w,
			nextval('w_envio_banco_seq'));

	end	loop;
	close	c02;

	/* trailer de lote */

	nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;

	ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||
				lpad(nr_lote_servico_w,4,'0') ||
				'5' ||
				rpad(' ',9,' ') ||
				lpad(nr_sequencia_w + 2,6,'0') ||
				lpad(somente_numero(to_char(coalesce(vl_total_w,0),'9999999999999990.00')),18,'0') ||
				'000000000000000000' ||
				'000000' ||
				rpad(' ',175,' ');

	insert	into w_envio_banco(cd_estabelecimento,
		ds_conteudo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres,
		nr_seq_apres_2,
		nr_sequencia)
	values (cd_estabelecimento_w,
		ds_conteudo_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		nr_seq_apres_w,
		nextval('w_envio_banco_seq'));

end	loop;
close	c01;

open	c03;
loop
fetch	c03 into
	ie_forma_lanc_w,
	ie_tipo_pagamento_w;
EXIT WHEN NOT FOUND; /* apply on c03 */

	/* header de lote - BLQ */

	nr_lote_servico_w	:= coalesce(nr_lote_servico_w,0) + 1;
	nr_seq_apres_w		:= coalesce(nr_seq_apres_w,0) + 1;
	nr_sequencia_w		:= 0;
	vl_total_w		:= 0;
	nr_versao_w		:= '041';

	if (ie_forma_lanc_w	= '16') or (ie_forma_lanc_w	= '17') or (ie_forma_lanc_w	= '11') then

		ie_tipo_servico_w	:= '22'; /*Pagamento de Contas, Tributos e Impostos*/
		
	else
		ie_tipo_servico_w	:= '20'; /*Pagamento de Fornecedores*/
		
	end if;

	ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||
				lpad(nr_lote_servico_w,4,'0') ||
				'1' ||
				'C' ||
				ie_tipo_servico_w||			--05.1
				ie_forma_lanc_w ||
				nr_versao_w ||				--07.1
				' ' ||					--08.1
				'2' ||
				cd_cgc_estab_w ||
				cd_convenio_banco_w ||
				'01' ||
				'0001' ||
				'01' ||
				rpad(' ',6,' ') ||
				cd_agencia_estab_w ||
				nr_conta_estab_w ||
				ie_digito_estab_w ||
				' ' ||
				nm_empresa_w ||
				rpad(' ',40,' ') ||
				ds_endereco_w ||
				nr_endereco_w ||
				ds_complemento_w ||
				ds_municipio_w ||
				nr_cep_w ||
				substr(sg_estado_w,1,2) ||
				rpad(' ',18,' ');

	insert	into w_envio_banco(cd_estabelecimento,
		ds_conteudo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres,
		nr_seq_apres_2,
		nr_sequencia)
	values (cd_estabelecimento_w,
		ds_conteudo_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		nr_seq_apres_w,
		nextval('w_envio_banco_seq'));

	open	c04;
	loop
	fetch	c04 into
		nm_pessoa_w,
		nr_titulo_w,
		vl_escritural_w,
		vl_acrescimo_w,
		vl_desconto_w,
		vl_despesa_w,
		dt_vencimento_w,
		vl_juros_w,
		vl_multa_w,
		nr_bloqueto_w,
		nr_nosso_numero_w,
		ie_tipo_identificacao_w,
		nr_inscricao_w;
	EXIT WHEN NOT FOUND; /* apply on c04 */

		nr_sequencia_w	:= coalesce(nr_sequencia_w,0) + 1;
		nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;
		qt_registro_w	:= coalesce(qt_registro_w,0) + 1;
		vl_total_w	:= coalesce(vl_total_w,0) + (coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0));

		select	count(*)
		into STRICT	qt_banco_w
		from	banco a
		where	CASE WHEN coalesce(a.cd_banco_externo::text, '') = '' THEN a.cd_banco  ELSE somente_numero(a.cd_banco_externo) END 	= somente_numero(substr(nr_bloqueto_w,1,3));

		/* segmento N - DARF normal */

		if (ie_forma_lanc_w	= '16') then

			select	max(b.dt_apuracao) dt_apuracao,
				max(b.dt_vencimento) dt_vencimento,
				lpad(substr(coalesce(max(b.cd_darf),'0'),1,6),6,' ') cd_darf,
				lpad(substr(coalesce(max(b.nr_referencia),'0'),1,17),17,'0') nr_referencia
			into STRICT	dt_apuracao_w,
				dt_vencimento_darf_w,
				cd_darf_w,
				nr_referencia_w
			from	darf b,
				darf_titulo_pagar a
			where	a.nr_seq_darf	= b.nr_sequencia
			and	a.nr_titulo	= nr_titulo_w;

			ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') || 				--01.3N
				lpad(nr_lote_servico_w,4,'0') ||			--02.3N
				'3' ||							--03.3N
				lpad(nr_sequencia_w,5,'0') ||				--04.3N
				'N' ||							--05.3N
				'0' ||							--06.3N
				'00' ||							--07.3N
				rpad(nr_titulo_w,20,' ') ||				--08.3N
				rpad(' ',20,' ') ||					--09.3N
				nm_empresa_w ||						--10.3N
				to_char(dt_remessa_retorno_w,'ddmmyyyy') ||		--11.3N
				lpad(somente_numero(to_char(coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0),'9999999999990.00')),15,'0') || --12.3N
				cd_darf_w ||						--01.3.N2
				ie_tipo_identificacao_w ||				--02.3.N2
				cd_cgc_estab_w ||					--03.3.N2
				'16' ||							--04.3.N2
				to_char(dt_apuracao_w,'ddmmyyyy') ||			--05.3.N2
				nr_referencia_w ||					--06.3.N2
				lpad(somente_numero(to_char(coalesce(vl_escritural_w,0),'9999999999990.00')),15,'0') ||	--07.3.N2
				lpad(somente_numero(to_char(coalesce(vl_multa_w,0),'9999999999990.00')),15,'0') ||		--08.3.N2
				lpad(somente_numero(to_char(coalesce(vl_juros_w,0),'9999999999990.00')),15,'0') ||		--09.3.N2
				to_char(dt_vencimento_darf_w,'ddmmyyyy') ||							--10.3.N2
				rpad(' ',18,' ') ||									--11.3.N2
				rpad(' ',10,' ');					--13.3N
		/* segmento N - GPS */

		elsif (ie_forma_lanc_w	= '17') then

			select	to_char(max(b.dt_referencia),'mmyyyy') dt_referencia,
				lpad(substr(coalesce(max(b.cd_pagamento),'0'),1,6),6,'0') cd_pagamento,
				max(b.vl_inss) vl_inss,
				max(b.vl_outras_entidades) vl_outras_entidades
			into STRICT	dt_referencia_w,
				cd_pagamento_w,
				vl_inss_w,
				vl_outras_entidades_w
			from	gps b,
				gps_titulo a
			where	a.nr_seq_gps	= b.nr_sequencia
			and	a.nr_titulo	= nr_titulo_w;

			ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') || 				--01.3N
				lpad(nr_lote_servico_w,4,'0') ||			--02.3N
				'3' ||							--03.3N
				lpad(nr_sequencia_w,5,'0') ||				--04.3N
				'N' ||							--05.3N
				'0' ||							--06.3N
				'00' ||							--07.3N
				rpad(nr_titulo_w,20,' ') ||				--08.3N
				rpad(' ',20,' ') ||					--09.3N
				nm_empresa_w ||						--10.3N
				to_char(dt_remessa_retorno_w,'ddmmyyyy') ||		--11.3N
				lpad(somente_numero(to_char(coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0),'9999999999990.00')),15,'0') || --12.3N
				cd_pagamento_w ||					--01.3.N1
				ie_tipo_identificacao_w ||				--02.3.N1
				cd_cgc_estab_w ||					--03.3.N1
				'17' ||							--04.3.N1
				dt_referencia_w ||					--05.3.N1
				lpad(somente_numero(to_char(coalesce(vl_inss_w,0),'9999999999990.00')),15,'0') ||  			--06.3.N1
				lpad(somente_numero(to_char(coalesce(vl_outras_entidades_w,0),'9999999999990.00')),15,'0') ||	--07.3.N1
				lpad('0',15,'0') ||					--08.3.N1
				rpad(' ',45,' ') ||					--09.3.N1
				rpad(' ',10,' ');					--13.3N
		/* segmento O */

		elsif (ie_forma_lanc_w	= '11') or (qt_banco_w		= 0) then

			ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||			--01.3O
				lpad(nr_lote_servico_w,4,'0') ||		--02.3O
				'3' ||						--03.3O
				lpad(nr_sequencia_w,5,'0') ||			--04.3O
				'O' ||						--05.3O
				'0' ||						--06.3O
				'00' ||						--07.3O
				nr_bloqueto_w ||				--08.3O
				nm_pessoa_w ||					--09.3O
				to_char(dt_vencimento_w,'ddmmyyyy') ||		--10.3O
				to_char(dt_remessa_retorno_w,'ddmmyyyy') ||	--11.3O
				lpad(somente_numero(to_char(coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0) + coalesce(vl_juros_w,0) + coalesce(vl_multa_w,0),'9999999999990.00')),15,'0') || -- 12.3O
				rpad(nr_titulo_w,20,' ') ||  			--13.3O
				rpad(' ',20,' ') ||				--14.3O
				rpad(' ',78,' ');				--15.3O
		/* segmento J */

		else

			begin
				dt_venc_bloqueto_w	:= to_date('07/10/1997','dd/mm/yyyy') + somente_numero(substr(nr_bloqueto_w,6,4));
			exception
			when	others then
				dt_venc_bloqueto_w	:= dt_vencimento_w;
			end;

			ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||
				lpad(nr_lote_servico_w,4,'0') ||
				'3' ||
				lpad(nr_sequencia_w,5,'0') ||
				'J' ||
				'000' ||
				nr_bloqueto_w ||
				nm_pessoa_w ||
				to_char(dt_venc_bloqueto_w,'ddmmyyyy') ||
				lpad(somente_numero(to_char(coalesce(vl_escritural_w,0),'9999999999990.00')),15,'0') ||
				lpad(somente_numero(to_char(coalesce(vl_desconto_w,0),'9999999999990.00')),15,'0') ||
				lpad(somente_numero(to_char(coalesce(vl_multa_w,0) + coalesce(vl_juros_w,0),'9999999999990.00')),15,'0') ||
				to_char(dt_remessa_retorno_w,'ddmmyyyy') ||
				lpad(somente_numero(to_char(coalesce(vl_escritural_w,0) - coalesce(vl_desconto_w,0) + coalesce(vl_acrescimo_w,0) + coalesce(vl_despesa_w,0),'9999999999990.00')),15,'0') ||
				'000000000000000' ||
				lpad(nr_titulo_w,6,'0') ||
				rpad(' ',14,' ') ||
				rpad(nr_nosso_numero_w,20,' ') ||
				'09' ||
				rpad(' ',16,' ');
        end if;

		insert	into w_envio_banco(cd_estabelecimento,
			ds_conteudo,
			dt_atualizacao,
			nm_usuario,
			nr_seq_apres,
			nr_seq_apres_2,
			nr_sequencia)
		values (cd_estabelecimento_w,
			ds_conteudo_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_apres_w,
			nr_seq_apres_w,
			nextval('w_envio_banco_seq'));

	end	loop;
	close	c04;

	/* trailer de lote */

	nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;

	ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||
				lpad(nr_lote_servico_w,4,'0') ||
				'5' ||
				rpad(' ',9,' ') ||
				lpad(nr_sequencia_w + 2,6,'0') ||
				lpad(somente_numero(to_char(coalesce(vl_total_w,0),'9999999999999990.00')),18,'0') ||
				'000000000000000000' ||
				'000000' ||
				rpad(' ',175,' ');

	insert	into w_envio_banco(cd_estabelecimento,
		ds_conteudo,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres,
		nr_seq_apres_2,
		nr_sequencia)
	values (cd_estabelecimento_w,
		ds_conteudo_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		nr_seq_apres_w,
		nextval('w_envio_banco_seq'));

end	loop;
close	c03;

/* trailer de arquivo */

nr_seq_apres_w	:= coalesce(nr_seq_apres_w,0) + 1;

ds_conteudo_w	:=	lpad(cd_banco_cobr_w,3,'0') ||
			'9999' ||
			'9' ||
			rpad(' ',9,' ') ||
			lpad(nr_lote_servico_w,6,'0') ||
			lpad(coalesce(qt_registro_w,0) + (coalesce(nr_lote_servico_w,0) * 2) + 2,6,'0') ||	/* registros detalhe + header e trailer dos lotes + header e trailer do arquivo */
			'000000' ||
			rpad(' ',205,' ');

insert	into w_envio_banco(cd_estabelecimento,
	ds_conteudo,
	dt_atualizacao,
	nm_usuario,
	nr_seq_apres,
	nr_seq_apres_2,
	nr_sequencia)
values (cd_estabelecimento_w,
	ds_conteudo_w,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_apres_w,
	nr_seq_apres_w,
	nextval('w_envio_banco_seq'));

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_pagto_caixa_240_v80 ( nr_seq_envio_p bigint, nm_usuario_p text) FROM PUBLIC;

