CREATE OR REPLACE PROCEDURE gerar_panorama_leitos ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_idioma_w		idioma.nr_sequencia%type;


BEGIN

nr_seq_idioma_w	:= OBTER_NR_SEQ_IDIOMA(nm_usuario_p);
CALL philips_param_pck.set_nr_seq_idioma(nr_seq_idioma_w);

/* Inicializar */

CALL panorama_leito_pck.inicializar(nm_usuario_p);
/* Gerar setores */

CALL panorama_leito_pck.gerar_w_pan_setor();
/* Gerar leitos */

CALL panorama_leito_pck.gerar_w_pan_leito(null);

/* Gerar pacientes */

CALL panorama_leito_pck.gerar_w_pan_paciente(null);
/* Gerar informações adicionais dos pacientes e leitos */

CALL panorama_leito_pck.gerar_w_pan_info_adicional('S',
											'S',
											cd_estabelecimento_p,
											null,
											nm_usuario_p);
/* Gerar ocorrências */

CALL panorama_leito_pck.gerar_w_pan_ocorrencia(cd_estabelecimento_p,null,nm_usuario_p);

/*Finalizar */

CALL panorama_leito_pck.finalizar();

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
