CREATE OR REPLACE PROCEDURE gerar_param_consulta_padrao (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_comando_w 		varchar(10000);
ds_comando_p 		varchar(10000);
nm_param_w		varchar(50);
pos_w			bigint;
ds_caracter_w		varchar(1);
contador_w		bigint 	:= 1;
caracteres_validos_w	varchar(60)  :='abcdefghijklmnopqrstuvxywzABCDEFGHIJKLMNOPRQSTUVXYWZ_';
ie_valido_w		boolean;
nr_seq_apres_w		bigint := 1;
cd_funcao_w		bigint;
qt_aux_w		bigint;

BEGIN
	select	ds_sql,
		cd_funcao
	into STRICT	ds_comando_w,
		cd_funcao_w
	from	dic_objeto
	where	nr_sequencia = nr_sequencia_p;

	update 	dic_objeto
	set	nr_seq_apres 	= nr_seq_apres * -1
	where	nr_seq_obj_sup 	= nr_sequencia_p;

	ds_comando_p := ds_comando_w;

	pos_w := position(':' in ds_comando_w);
	while( pos_w > 0 ) and ( contador_w < 300 ) loop /*Variavel de controle para evitar LOOP*/
		ds_comando_w 	:= substr(ds_comando_w,pos_w+1,length(ds_comando_w));
		nm_param_w 	:= '';
		ie_valido_w	:= true;
		pos_w 		:= 1;
		while( ie_valido_w ) and ( pos_w < length(ds_comando_p)) loop  /*Variavel de controle para evitar LOOP*/
			ds_caracter_w := substr(ds_comando_w,pos_w,1);
			if ( position(ds_caracter_w in caracteres_validos_w) > 0 ) then
				nm_param_w := nm_param_w || ds_caracter_w;
			else
				ie_valido_w := false;
			end if;
			pos_w := pos_w + 1;
		end loop;
		nm_param_w := upper(nm_param_w);
		if ( position('_' in nm_param_w) > 0 ) then
/*
			select 	count(*)
			into	qt_aux_w
			from	dic_objeto
			where	nr_seq_obj_sup = nr_sequencia_p
			and	nm_campo_base = nm_param_w;

*/
			insert into dic_objeto(
				nr_sequencia,
				nr_seq_obj_sup,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				nm_campo_base,
				nr_seq_apres,
				ie_tipo_objeto,
				cd_funcao
			) values (
				nextval('dic_objeto_seq'),
				nr_sequencia_p,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_param_w,
				nr_seq_apres_w,
				'PSQL',
				cd_funcao_w
			);

			nr_seq_apres_w := nr_seq_apres_w + 1;
		end if;
		ds_comando_w := substr(ds_comando_w,pos_w,length(ds_comando_w));
		pos_w := position(':' in ds_comando_w);
		contador_w := contador_w + 1;
	end loop;

	delete 	from dic_objeto
	where	nr_seq_obj_sup 	= nr_sequencia_p
	and	nr_seq_apres < 0;

	commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
