SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_parecer_encaminhamento ( nr_seq_encaminhamento_p bigint, nr_seq_parecer_p INOUT bigint) AS $body$
DECLARE



cd_pessoa_fisica_w	atend_encaminhamento.cd_pessoa_fisica%type;
ds_justificativa_w	atend_encaminhamento.ds_observacao%type;
nr_atendimento_w	atend_encaminhamento.nr_atendimento%type;
cd_medico_dest_w	atend_encaminhamento.cd_medico_dest%type;
dt_avaliacao_w		atend_encaminhamento.dt_liberacao%type;
cd_especialidade_w	atend_encaminhamento.cd_especialidade%type;
ds_titulo_w			med_tipo_avaliacao.ds_tipo%type;

nr_seq_parecer_w	parecer_medico_req.nr_parecer%type;

nm_usuario_w		usuario.nm_usuario%type;
cd_pf_usuario_w		usuario.cd_pessoa_fisica%type;

ds_fonte_w			varchar(100);
ds_tam_fonte_w		varchar(10);
nr_tam_fonte_w		integer;

ds_cabecalho_w		varchar(32000);
ds_conteudo_w		varchar(32000);
ds_rodape_w		 	varchar(32000);
ds_titulo_rft_w 	varchar(255) := '';
ds_enter_w			varchar(10)	:=  '<br>';

ds_parecer_w		varchar(32000);

enviar_CI_w			varchar(1);
enviar_email_w		varchar(1);
ds_profissional_w	varchar(255);
ds_especialidade_w  varchar(255);
ds_expressao_w		varchar(255);	


c01 CURSOR FOR
	SELECT	c.nr_sequencia,
			coalesce(ds_label_relat,c.ds_item) ds_item,
			c.IE_RESULTADO,
			coalesce((SELECT d.ds_resultado from regulacao_avaliacao_result d where d.nr_seq_item = c.nr_sequencia and obter_somente_numero(Obter_Result_Avaliacao_Enc(a.nr_sequencia,c.nr_sequencia)) = d.nr_seq_avaliacao and coalesce(c.cd_dominio::text, '') = ''),substr(Obter_Result_Avaliacao_Enc(a.nr_sequencia,c.nr_sequencia),1,4000)) ds_resultado,
			cd_dominio,
			c.ds_complemento ds_complemento
	from	atend_encaminhamento a,
			med_tipo_avaliacao b,
			med_item_avaliar c
	where	b.nr_sequencia = c.nr_seq_tipo
	and	coalesce(c.ie_situacao, 'A') = 'A'
	and	a.NR_SEQ_AVALIACAO = b.nr_sequencia 	
	and	a.nr_sequencia = nr_seq_encaminhamento_p
	order by c.nr_seq_apresent;	
	

c01_w	c01%rowtype;
	
						

BEGIN

	nm_usuario_w := wheb_usuario_pck.get_nm_usuario;
	cd_pf_usuario_w := obter_pessoa_fisica_usuario(nm_usuario_w, 'C');

	select	max(a.cd_pessoa_fisica),
			max(a.nr_atendimento),
			max(a.cd_medico_dest),
			max(a.dt_liberacao),
			max(a.ds_observacao),
			max(a.cd_especialidade),
			max(b.ds_tipo)	
	into STRICT	cd_pessoa_fisica_w,
			nr_atendimento_w,
			cd_medico_dest_w,
			dt_avaliacao_w,
			ds_justificativa_w,
			cd_especialidade_w,
			ds_titulo_w
	FROM atend_encaminhamento a
LEFT OUTER JOIN med_tipo_avaliacao b ON (a.NR_SEQ_AVALIACAO = b.nr_sequencia)
WHERE a.nr_sequencia = nr_seq_encaminhamento_p;

	if (dt_avaliacao_w IS NOT NULL AND dt_avaliacao_w::text <> '') then
		
		ds_fonte_w := Obter_Param_Usuario(281, 5, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, ds_fonte_w);
		ds_tam_fonte_w := Obter_Param_Usuario(281, 6, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, ds_tam_fonte_w);
		
		

		open c01;
		loop
		fetch C01 into	
			c01_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			
			
				if (c01_w.ie_resultado in ('X','T')) then
					ds_conteudo_w	:= ds_conteudo_w || '<b>' || c01_w.ds_item || '</b>'  || ds_enter_w;
					if ((trim(both c01_w.ds_complemento) IS NOT NULL AND (trim(both c01_w.ds_complemento))::text <> '')) and (c01_w.ie_resultado in ('X')) then
						ds_conteudo_w	:= ds_conteudo_w || '<b>' || c01_w.ds_complemento || '</b>' || ds_enter_w;
					end if;
				elsif (c01_w.ie_resultado = 'B') then
					if (c01_w.ds_resultado	= 'S') or (c01_w.ds_resultado	= '1') then
						c01_w.ds_resultado	:= wheb_mensagem_pck.get_texto(298977,null); --'Sim';
					else
						c01_w.ds_resultado	:= wheb_mensagem_pck.get_texto(298976,null); --'NÃ£o';
					end if;
					ds_conteudo_w := ds_conteudo_w || ' ' || '<b>' || c01_w.ds_item || ' : ' || '</b>' || c01_w.ds_resultado || ds_enter_w;
				elsif (c01_w.ie_resultado = 'O') then
						if (c01_w.cd_dominio > 0) then
							select	max(ds_valor_dominio)
							into STRICT	c01_w.ds_resultado
							from	med_valor_dominio
							where	nr_seq_dominio = c01_w.cd_dominio
							and		vl_dominio = c01_w.ds_resultado;

							ds_conteudo_w := ds_conteudo_w || ' ' || '<b>' || c01_w.ds_item || ' : ' || '</b>' || c01_w.ds_resultado || ds_enter_w;
						end if;
				elsif (c01_w.ie_resultado in ('V','C')) then
					ds_conteudo_w := ds_conteudo_w ||' '|| '<b>' || c01_w.ds_item || ' : ' || '</b>' || c01_w.ds_resultado || ' ' || c01_w.ds_complemento || ds_enter_w;
				else
					ds_conteudo_w := ds_conteudo_w ||' '|| '<b>' || c01_w.ds_item || ' : ' || '</b> '|| c01_w.ds_resultado ||ds_enter_w;
				end if;
			

		end loop;
		close C01;

		if (cd_medico_dest_w IS NOT NULL AND cd_medico_dest_w::text <> '') then
		
			if (ds_conteudo_w IS NOT NULL AND ds_conteudo_w::text <> '') then
				ds_conteudo_w := ds_conteudo_w || ds_enter_w;
			end if;
			
			Select  obter_nome_medico(cd_medico_dest_w,'n'),
					obter_desc_expressao(296509)
			into STRICT 	ds_profissional_w,
					ds_expressao_w
			;
			
			
			ds_conteudo_w := ds_conteudo_w || '<b>' || ds_expressao_w || ' : ' || '</b>' || ds_enter_w || ds_profissional_w ||ds_enter_w;
		end if;
		
		if (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') then
		
			if (ds_conteudo_w IS NOT NULL AND ds_conteudo_w::text <> '') then
				ds_conteudo_w := ds_conteudo_w || ds_enter_w;
			end if;
			
			Select max(ds_especialidade),
					max(obter_desc_expressao(289431))
			into STRICT 	ds_especialidade_w,
					ds_expressao_w
			from   especialidade_medica
			where	cd_especialidade = cd_especialidade_w;
			
			
			ds_conteudo_w := ds_conteudo_w || '<b>' || ds_expressao_w || ' : ' || '</b>' || ds_enter_w || ds_especialidade_w ||ds_enter_w;
		end if;
			
		
		if (ds_justificativa_w IS NOT NULL AND ds_justificativa_w::text <> '') then
			if (ds_conteudo_w IS NOT NULL AND ds_conteudo_w::text <> '') then
				ds_conteudo_w := ds_conteudo_w || ds_enter_w;
			end if;
			ds_conteudo_w := ds_conteudo_w || '<b>' || wheb_mensagem_pck.get_texto(1011867,null) || ' : ' || '</b>' || ds_enter_w || ds_justificativa_w ||ds_enter_w;
		end if;
		
		Select 	obter_desc_expressao(317061)
		into STRICT	ds_titulo_w
		;
		
		
		
		if (ds_titulo_w IS NOT NULL AND ds_titulo_w::text <> '') then			
			ds_titulo_w := '<b>' ||  '<center>' || ds_titulo_w || '</b>'||'</center>' || '<br>'|| '<br>';				
		end if;
		
		

	
		ds_parecer_w	:= ds_titulo_w ||ds_conteudo_w;	
		
		select	nextval('parecer_medico_req_seq')
		into STRICT	nr_seq_parecer_w
		;
		
		insert into parecer_medico_req(
							nr_parecer,
							nr_atendimento,
							cd_medico,
							cd_especialidade,
							dt_atualizacao,
							nm_usuario,
							cd_especialidade_dest,
							ds_motivo_consulta,
							dt_liberacao,
							cd_perfil_ativo,
							cd_pessoa_parecer,
							ie_situacao,
							cd_pessoa_fisica,
							ie_tipo_parecer,
							nm_usuario_nrec,
							dt_atualizacao_nrec
						) values (
							nr_seq_parecer_w,
							nr_atendimento_w,
							cd_pf_usuario_w,
							obter_especialidade_medico(cd_pf_usuario_w, 'C'),
							clock_timestamp(),
							nm_usuario_w,
							cd_especialidade_w,
							ds_parecer_w,
							clock_timestamp(),
							wheb_usuario_pck.get_cd_perfil,
							cd_medico_dest_w,
							'A',
							cd_pessoa_fisica_w,
							'M',
							nm_usuario_w,
							clock_timestamp()
						);
						
		nr_seq_parecer_p := nr_seq_parecer_w;
		
		commit;
	

		enviar_CI_w := Obter_Param_Usuario(281, 361, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, enviar_CI_w);
		if (enviar_CI_w <> 'N') then
			begin
				CALL enviar_comunic_parecer_html(nr_seq_parecer_w,1, wheb_usuario_pck.get_cd_estabelecimento, 0, nm_usuario_w);
			Exception when others then
				null;
				
			end;
		end if;
		
		enviar_email_w := Obter_Param_Usuario(281, 1167, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, enviar_email_w);
		if (enviar_email_w <> 'N') then
			begin
				CALL enviar_email_parecer_med(nr_seq_parecer_w,1, wheb_usuario_pck.get_cd_estabelecimento, 0, nm_usuario_w);
		Exception when others then
				null;
			end;
		end if;
		
		
	end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_parecer_encaminhamento ( nr_seq_encaminhamento_p bigint, nr_seq_parecer_p INOUT bigint) FROM PUBLIC;

