SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_parecer_evolucao ( nr_seq_encaminhamento_p bigint, ds_conteudo_p text, nr_seq_regulacao_p bigint, nr_seq_parecer_p INOUT bigint) AS $body$
DECLARE



cd_pessoa_fisica_w	parecer_medico_req.cd_pessoa_fisica%type;
nr_atendimento_w	parecer_medico_req.nr_atendimento%type;
cd_especialidade_w	parecer_medico_req.cd_especialidade%type;
dt_avaliacao_w		evolucao_paciente.dt_liberacao%type;
ds_titulo_w			varchar(255);

nr_seq_parecer_w	parecer_medico_req.nr_parecer%type;
nm_usuario_w		usuario.nm_usuario%type;

cd_pessoa_parecer_w varchar(10);
cd_medico_w			varchar(10);

enviar_CI_w			varchar(1);
enviar_email_w		varchar(1);



BEGIN

	nm_usuario_w := wheb_usuario_pck.get_nm_usuario;

	SELECT	MAX(a.cd_pessoa_fisica),
			MAX(a.nr_atendimento),
			MAX(a.dt_liberacao),
			MAX(a.cd_especialidade),
			MAX(a.cd_medico_parecer),
			max(a.cd_medico)
	INTO STRICT	cd_pessoa_fisica_w,
			nr_atendimento_w,
			dt_avaliacao_w,
			cd_especialidade_w,
			cd_pessoa_parecer_w,
			cd_medico_w
	FROM	evolucao_paciente a
	WHERE	a.cd_evolucao = nr_seq_encaminhamento_p;

	if (dt_avaliacao_w IS NOT NULL AND dt_avaliacao_w::text <> '') then

		select	nextval('parecer_medico_req_seq')
		into STRICT	nr_seq_parecer_w
		;

		insert into parecer_medico_req(
							nr_parecer,
							nr_atendimento,
							cd_medico,
							cd_pessoa_parecer,
							cd_especialidade,
							dt_atualizacao,
							nm_usuario,
							dt_liberacao,
							cd_perfil_ativo,
							ie_situacao,
							cd_pessoa_fisica,
							ie_tipo_parecer,
							nm_usuario_nrec,
							dt_atualizacao_nrec
						) values (
							nr_seq_parecer_w,
							nr_atendimento_w,
							cd_medico_w,
							cd_pessoa_parecer_w,
							cd_especialidade_w,
							clock_timestamp(),
							nm_usuario_w,
							clock_timestamp(),
							wheb_usuario_pck.get_cd_perfil,
							'A',
							cd_pessoa_fisica_w,
							'M',
							nm_usuario_w,
							clock_timestamp()
						);

		nr_seq_parecer_p := nr_seq_parecer_w;

		commit;

		CALL copia_campo_long_de_para(	'EVOLUCAO_PACIENTE',
					'DS_EVOLUCAO',
					' where cd_evolucao = :cd_evolucao_seq ',
					'cd_evolucao_seq='||nr_seq_encaminhamento_p,
					'PARECER_MEDICO_REQ',
					'DS_MOTIVO_CONSULTA',
					' where nr_parecer = :nr_parecer_seq',
					'nr_parecer_seq='||nr_seq_parecer_w);


		enviar_CI_w := Obter_Param_Usuario(281, 361, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, enviar_CI_w);
		if (enviar_CI_w <> 'N') then
			begin
				CALL enviar_comunic_parecer_html(nr_seq_parecer_w,1, wheb_usuario_pck.get_cd_estabelecimento, 0, nm_usuario_w);
			Exception when others then
				null;

			end;
		end if;

		enviar_email_w := Obter_Param_Usuario(281, 1167, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, enviar_email_w);
		if (enviar_email_w <> 'N') then
			begin
				CALL enviar_email_parecer_med(nr_seq_parecer_w,1, wheb_usuario_pck.get_cd_estabelecimento, 0, nm_usuario_w);
		Exception when others then
				null;
			end;
		end if;


	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_parecer_evolucao ( nr_seq_encaminhamento_p bigint, ds_conteudo_p text, nr_seq_regulacao_p bigint, nr_seq_parecer_p INOUT bigint) FROM PUBLIC;

