CREATE OR REPLACE PROCEDURE gerar_parecer_med_integracao ( nr_parecer_p text, nr_origem_p text, cd_medico_p text, ie_status_p text, ds_parecer_p text, nr_seq_regulacao_p text, nr_seq_interno_p text, nm_usuario_p text default null) AS $body$
DECLARE


nm_usuario_w	varchar(15);			
nr_sequencia_w	bigint;		


BEGIN

Select  max('T.I.E') -- Integração Bifrost
into STRICT	nm_usuario_w
;

if (nr_origem_p IS NOT NULL AND nr_origem_p::text <> '' AND cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then

select	max(nr_sequencia) + 1
into STRICT	nr_sequencia_w
from 	parecer_medico 
where 	nr_parecer  = nr_parecer_p;

insert into parecer_medico(nr_seq_interno,
	nr_sequencia,
	nr_parecer,
	cd_medico,
	dt_atualizacao,            
	nm_usuario,                
	ds_parecer,               
	dt_liberacao,              
	ie_situacao,               
	ie_status,       
	nr_seq_regulacao)
values (
	nextval('parecer_medico_seq'),
	nr_sequencia_w,
	nr_origem_p,
	cd_medico_p,
	clock_timestamp(),
	nm_usuario_w,
	ds_parecer_p,
	clock_timestamp(),
	'A',
	ie_status_p,
	nr_seq_regulacao_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
