CREATE OR REPLACE PROCEDURE gerar_particip_proc (nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_proc_w	bigint;
nr_sequencia_w	bigint;



c01 CURSOR FOR
SELECT	a.nr_sequencia
from 	procedimento_paciente a
where	a.nr_cirurgia = nr_cirurgia_p
and	a.nr_sequencia <> nr_sequencia_proc_w
and	not exists (select 1 from cirurgia_participante x where x.nr_seq_procedimento = a.nr_sequencia);



BEGIN

select 	max(a.nr_sequencia)
into STRICT	nr_sequencia_proc_w
from	procedimento_paciente a
where	a.nr_cirurgia	=	nr_cirurgia_p
and	exists (SELECT 1 from cirurgia_participante x where x.nr_seq_procedimento = a.nr_sequencia);

if (coalesce(nr_sequencia_proc_w::text, '') = '') then
	select 	max(nr_sequencia)
	into STRICT	nr_sequencia_proc_w
	from	procedimento_paciente
	where	nr_cirurgia	=	nr_cirurgia_p;
end if;

update	cirurgia_participante
set	nr_seq_procedimento	=	nr_sequencia_proc_w
where	nr_cirurgia		=	nr_cirurgia_p
and	coalesce(nr_seq_procedimento::text, '') = '';

commit;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL gerar_copia_participantes(nr_cirurgia_p, nr_sequencia_proc_w, nr_sequencia_w, nm_usuario_p);
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
