CREATE OR REPLACE PROCEDURE gerar_passagem_ageserv (nr_atendimento_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
				 
nr_seq_interno_w	bigint	:= 0;				
cd_setor_atendimento_w	bigint;
				

BEGIN 
 
 
if (nr_seq_agenda_p > 0) and (nr_atendimento_p > 0) then 
						 
	select	max(cd_setor_atendimento) 
	into STRICT	cd_setor_atendimento_w 
	from	agenda_consulta 
	where	nr_sequencia = nr_seq_agenda_p;
	 
	if (cd_setor_atendimento_w > 0) then 
	 
		CALL gerar_passagem_setor_atend(nr_atendimento_p, cd_setor_atendimento_w, clock_timestamp(), 'S', nm_usuario_p);
 
		select	coalesce(max(nr_seq_interno),0) 
		into STRICT	nr_seq_interno_w 
		from 	atend_paciente_unidade 
		where 	nr_atendimento	= nr_atendimento_p;
		 
		CALL atend_paciente_unid_afterpost(nr_seq_interno_w,'I',nm_usuario_p);				
		 
	end if;
						 
end if;
 
commit;
 
end;
