CREATE OR REPLACE PROCEDURE gerar_passagem_setor_s_agfa ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_unidade_w	bigint;
nr_sequencia_w		bigint;
nr_seq_interno_w	bigint;


BEGIN

select	coalesce(min(nr_seq_interno),0)
into STRICT	nr_seq_unidade_w
from	unidade_atendimento
where	ie_situacao = 'A'
and	cd_setor_atendimento	= cd_setor_atendimento_p;

select	coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_sequencia_w
from	atend_paciente_unidade
where	nr_atendimento = nr_atendimento_p;

select	nextval('atend_paciente_unidade_seq')
into STRICT	nr_seq_interno_w
;

insert into Atend_Paciente_unidade(
	nr_atendimento, cd_setor_atendimento, cd_unidade_basica,
	cd_unidade_compl, dt_entrada_unidade, dt_saida_unidade, dt_atualizacao,
	nm_usuario, nr_sequencia, dt_saida_interno, nr_seq_interno)
SELECT
	nr_atendimento_p, cd_setor_atendimento_p, cd_unidade_basica,
	cd_unidade_compl, clock_timestamp(), clock_timestamp(), clock_timestamp(),
	nm_usuario_p, nr_sequencia_w, clock_timestamp(), nr_seq_interno_w
from	unidade_atendimento
where	nr_seq_interno	= nr_seq_unidade_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
