CREATE OR REPLACE PROCEDURE gerar_pedido_parecer_rn ( cd_medico_p text, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_espec_dest_w		integer := 0;
cd_pessoa_fisica_w	varchar(10);


BEGIN 
cd_espec_dest_w := obter_param_usuario(281, 1242, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, cd_espec_dest_w);
if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (cd_espec_dest_w > 0) then 
	 
	select	max(cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	insert into 	parecer_medico_req(	 
				nr_parecer, 
				cd_medico, 
				dt_atualizacao, 
				nm_usuario, 
				nr_atendimento, 
				cd_especialidade, 
				cd_especialidade_dest, 
				cd_pessoa_fisica, 
				ie_situacao) 
		values (	nextval('parecer_medico_req_seq'), 
				cd_medico_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				nr_atendimento_p, 
				null, 
				cd_espec_dest_w, 
				cd_pessoa_fisica_w, 
				'A');
end if;
 
commit;
 
end;
$body$
