CREATE OR REPLACE PROCEDURE gerar_pendencias_internas_pc ( nr_seq_escuta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_prof_pend_w		varchar(10);
nr_seq_programa_saude_w	bigint;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	atend_encaminhamento a 
	where	a.nr_seq_escuta = nr_seq_escuta_p 
	and	a.ie_gerar_pendencia = 'S';

C02 CURSOR FOR 
	SELECT	nr_sequencia 
	from	atend_programa_saude a 
	where	a.nr_seq_escuta = nr_seq_escuta_p 
	and	a.ie_situacao = 'A';


BEGIN 
 
if (nr_seq_escuta_p IS NOT NULL AND nr_seq_escuta_p::text <> '') then 
	open C01;
	loop 
	fetch C01 into	 
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL gerar_lista_espera_pep(nr_sequencia_w,'ATEND_ENCAMINHAMENTO',nm_usuario_p);
		end;
	end loop;
	close C01;
 
	open C02;
	loop 
	fetch C02 into	 
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		CALL gerar_lista_espera_pep(nr_sequencia_w,'ATEND_PROGRAMA_SAUDE',nm_usuario_p);
		end;
	end loop;
	close C02;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
