CREATE OR REPLACE PROCEDURE gerar_pendencias_mipres ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_seq_prescr_mipres_p prescr_mipres.nr_sequencia%type default null, dt_validity_mipres_p prescr_mipres.dt_validity_mipres%type default null, nr_prescr_mipres_p prescr_mipres.nr_presc_mipres%type default null, nr_sequencia_gerado_p INOUT prescr_mipres.nr_sequencia%type DEFAULT NULL) AS $body$
DECLARE


nr_seq_prescr_mipres_w	prescr_mipres.nr_sequencia%type;
cd_pessoa_fisica_mipres_w	pessoa_fisica.cd_pessoa_fisica%type;


BEGIN

nr_sequencia_gerado_p := 0;

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	if (nr_prescr_mipres_p IS NOT NULL AND nr_prescr_mipres_p::text <> '') then
	
		select cd_pessoa_fisica
		into STRICT cd_pessoa_fisica_mipres_w
		from prescr_mipres
		where nr_presc_mipres = nr_prescr_mipres_p;
		
		if (cd_pessoa_fisica_mipres_w <> cd_pessoa_fisica_p) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1215521);
		end if;
	
	end if;

	if (coalesce(nr_seq_prescr_mipres_p::text, '') = '') then
		
		select max(nr_sequencia)+1
		into STRICT nr_seq_prescr_mipres_w
		from prescr_mipres;
		
		insert into prescr_mipres(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario, 
		dt_atualizacao_nrec,            
		nm_usuario_nrec,  
		cd_pessoa_fisica,
		nr_presc_mipres,
		dt_validity_mipres,
		ie_pendency_origin,
		cd_funcao,
		ie_status)
		values (
		nr_seq_prescr_mipres_w,
		clock_timestamp(),
		obter_usuario_ativo,
		clock_timestamp(),
		obter_usuario_ativo,
		cd_pessoa_fisica_p,
		nr_prescr_mipres_p,
		dt_validity_mipres_p,
		CASE WHEN obter_funcao_ativa='281' THEN  'ONC' WHEN obter_funcao_ativa='3030' THEN  'RAD'  ELSE 'PRESCR' END ,
		obter_funcao_ativa,
		2);
		
		nr_sequencia_gerado_p := nr_seq_prescr_mipres_w;
		
	else
	
	update prescr_mipres
	set dt_atualizacao = clock_timestamp(),
		nm_usuario = obter_usuario_ativo,
		cd_pessoa_fisica = cd_pessoa_fisica_p,
		nr_presc_mipres = nr_prescr_mipres_p,
		dt_validity_mipres = dt_validity_mipres_p,
		ie_pendency_origin = CASE WHEN obter_funcao_ativa='281' THEN  'ONC' WHEN obter_funcao_ativa='3030' THEN  'RAD'  ELSE 'PRESCR' END ,
		cd_funcao = obter_funcao_ativa,
		ie_status = 2
		where nr_sequencia = nr_seq_prescr_mipres_p;
	
	end if;
	
end if;

end;
$body$
