SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_pendencia_auditoria ( nr_seq_auditoria_p bigint, nr_seq_tipo_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
nr_interno_conta_w	bigint;
nr_atendimento_w	bigint;
dt_inicio_auditoria_w	timestamp;
dt_final_auditoria_w	timestamp;
cd_setor_atendimento_w	integer;
nr_seq_estagio_w	bigint;
dt_pendencia_w		timestamp;
nr_seq_regra_resp_w	bigint;
nr_seq_regra_item_w	bigint;
nr_seq_pendencia_w	bigint;
nr_seq_estagio_param_w	bigint;
nr_seq_audit_vinc_w	bigint;
cd_estabelecimento_w	conta_paciente.cd_estabelecimento%type;
ie_gerar_pendencia_w	varchar(1);
				

BEGIN 
 
if (coalesce(nr_seq_auditoria_p,0) <> 0) then 
 
	select	max(nr_interno_conta), 
		max(nr_atendimento), 
		max(dt_periodo_inicial), 
		max(dt_periodo_final), 
		max(obter_setor_atendimento(nr_atendimento)) 
	into STRICT	nr_interno_conta_w, 
		nr_atendimento_w, 
		dt_inicio_auditoria_w, 
		dt_final_auditoria_w, 
		cd_setor_atendimento_w 
	from	auditoria_conta_paciente 
	where	nr_sequencia = nr_seq_auditoria_p;
	 
	select 	coalesce(max(cd_estabelecimento),wheb_usuario_pck.get_cd_estabelecimento) 
	into STRICT	cd_estabelecimento_w 
	from 	conta_paciente 
	where 	nr_interno_conta = nr_interno_conta_w;
	 
	ie_gerar_pendencia_w:= obter_valor_param_usuario(1116, 136, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_w);
	 
	nr_seq_estagio_param_w:= 0;
	 
	select 	coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_audit_vinc_w 
	from 	auditoria_conta_paciente 
	where 	nr_atendimento = nr_atendimento_w 
	and 	nr_seq_audit_vinc = nr_seq_auditoria_p;
	 
	if (nr_seq_audit_vinc_w > 0) then 
		nr_seq_estagio_param_w	:= somente_numero(obter_valor_param_usuario(1116, 184, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_w));
	end if;
	 
	if (nr_seq_estagio_param_w = 0) then 
 
		select	min(nr_sequencia) 
		into STRICT	nr_seq_estagio_w 
		from	cta_estagio_pend 
		where	coalesce(ie_situacao,'A') = 'A' 
		and	ie_tipo_estagio = 'A' 
		and	cta_obter_se_estagio_lib(nr_sequencia, obter_perfil_ativo, nm_usuario_p, nr_seq_tipo_p) = 'S';
		 
	else 
	 
		nr_seq_estagio_w:= nr_seq_estagio_param_w;
		 
	end if;
	 
	dt_pendencia_w	:= clock_timestamp();
	 
	SELECT * FROM cta_obter_regra_resp_pend(nr_seq_tipo_p, nr_seq_estagio_w, dt_pendencia_w, nr_seq_regra_resp_w, nr_seq_regra_item_w) INTO STRICT nr_seq_regra_resp_w, nr_seq_regra_item_w;
	 
	if (coalesce(nr_seq_regra_resp_w,0) <> 0) then 
	 
		select	nextval('cta_pendencia_seq') 
		into STRICT	nr_seq_pendencia_w 
		;
	 
		insert into cta_pendencia( 
			nr_sequencia, 
			nr_atendimento, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			dt_pendencia, 
			nr_seq_tipo, 
			nr_seq_estagio, 
			nr_interno_conta, 
			cd_setor_atendimento, 
			nr_seq_regra_resp, 
			dt_inicio_auditoria, 
			dt_final_auditoria, 
			nr_seq_auditoria 
		) values ( 
			nr_seq_pendencia_w, 
			nr_atendimento_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			dt_pendencia_w, 
			nr_seq_tipo_p, 
			nr_seq_estagio_w, 
			nr_interno_conta_w, 
			cd_setor_atendimento_w, 
			nr_seq_regra_resp_w, 
			dt_inicio_auditoria_w, 
			dt_final_auditoria_w, 
			nr_seq_auditoria_p 
		);
		 
		CALL gerar_cta_pendencia_audit(nr_seq_pendencia_w, nr_seq_auditoria_p, nm_usuario_p);
		 
		if (ie_gerar_pendencia_w = 'V') then 
			CALL Cta_registrar_historico_pend(nr_seq_pendencia_w, nr_seq_estagio_w, null, '1', nm_usuario_p);
		end if;
		 
		CALL cta_enviar_email(nr_atendimento_w,nr_interno_conta_w,nr_seq_pendencia_w,nr_seq_tipo_p,nr_seq_estagio_w,'A',null,nm_usuario_p);
		 
	else	 
		ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(281437);
	end if;
		 
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_pendencia_auditoria ( nr_seq_auditoria_p bigint, nr_seq_tipo_p bigint, nm_usuario_p text, ds_erro_p INOUT text) FROM PUBLIC;

