CREATE OR REPLACE PROCEDURE gerar_pendencia_conta (nr_interno_conta_p bigint, nr_seq_tipo_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
nr_interno_conta_w   bigint;
nr_atendimento_w    bigint;
cd_setor_atendimento_w integer;
nr_seq_estagio_w    bigint;
dt_pendencia_w     timestamp;
nr_seq_regra_resp_w   bigint;
nr_seq_regra_item_w   bigint;
nr_seq_pendencia_w   bigint;
cd_funcao_w		funcao.cd_funcao%type	:= wheb_usuario_pck.get_cd_funcao;
ds_complemento_w	varchar(255);


BEGIN 
 
if (coalesce(nr_interno_conta_p,0) <> 0) then 
 
    select max(nr_interno_conta), 
        max(nr_atendimento), 
        max(obter_setor_atendimento(nr_atendimento)) 
    into STRICT  nr_interno_conta_w, 
        nr_atendimento_w, 
        cd_setor_atendimento_w 
    from  conta_paciente 
    where  nr_interno_conta = nr_interno_conta_p;
 
    select min(nr_sequencia) 
    into STRICT  nr_seq_estagio_w 
    from  cta_estagio_pend 
    where  coalesce(ie_situacao,'A') = 'A' 
    and   ie_tipo_estagio = 'A' 
    and	 cta_obter_se_estagio_lib(nr_sequencia, obter_perfil_ativo, nm_usuario_p, nr_seq_tipo_p) = 'S';
 
    dt_pendencia_w := clock_timestamp();
 
    SELECT * FROM cta_obter_regra_resp_pend(nr_seq_tipo_p, nr_seq_estagio_w, dt_pendencia_w, nr_seq_regra_resp_w, nr_seq_regra_item_w) INTO STRICT nr_seq_regra_resp_w, nr_seq_regra_item_w;
 
	if (cd_funcao_w = 290) then 
		ds_complemento_w	:= WHEB_MENSAGEM_PCK.get_texto(281655);
	else 
		ds_complemento_w	:= WHEB_MENSAGEM_PCK.get_texto(281656);
	end if;
 
    if (coalesce(nr_seq_regra_resp_w,0) <> 0) then 
 
        select nextval('cta_pendencia_seq') 
        into STRICT  nr_seq_pendencia_w 
;
 
        insert into cta_pendencia( 
            nr_sequencia, 
            nr_atendimento, 
            dt_atualizacao, 
            nm_usuario, 
            dt_atualizacao_nrec, 
            nm_usuario_nrec, 
            dt_pendencia, 
            nr_seq_tipo, 
            nr_seq_estagio, 
            nr_interno_conta, 
            cd_setor_atendimento, 
            nr_seq_regra_resp, 
			ds_complemento 
        ) values ( 
            nr_seq_pendencia_w, 
            nr_atendimento_w, 
            clock_timestamp(), 
            nm_usuario_p, 
            clock_timestamp(), 
            nm_usuario_p, 
            dt_pendencia_w, 
            nr_seq_tipo_p, 
            nr_seq_estagio_w, 
            nr_interno_conta_w, 
            cd_setor_atendimento_w, 
            nr_seq_regra_resp_w, 
			ds_complemento_w 
        );
 
		CALL cta_enviar_email(nr_atendimento_w,nr_interno_conta_w,nr_seq_pendencia_w,nr_seq_tipo_p,nr_seq_estagio_w,'A',null,nm_usuario_p);
 
    else 
        ds_erro_p    := WHEB_MENSAGEM_PCK.get_texto(281657);
    end if;
 
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
