SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_pend_assinatura_pep ( nr_seq_atendimento_p bigint, nr_seq_material_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w bigint;
nr_seq_interno_w bigint;
cd_pessoa_fisica_w varchar(10);



BEGIN
	begin
		if ((nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '')
			and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '')
			and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '')) then

			-- buscar o atendimento do medicamento que foi checado
			select  max(a.nr_atendimento)
			into STRICT    nr_atendimento_w
			from    paciente_atendimento a
			where   a.nr_seq_atendimento = nr_seq_atendimento_p;

			-- buscar cd_pessoa_fisica do paciente que recebeu este medicamento
			select  max(s.cd_pessoa_fisica)
			into STRICT    cd_pessoa_fisica_w
			from    paciente_atendimento a,
					paciente_setor s
			where   a.nr_seq_atendimento = nr_seq_atendimento_p
			and     a.nr_seq_paciente = s.nr_seq_paciente;

			-- buscar o nr_seq_interno do medicamento
			select  max(a.nr_seq_interno)
			into STRICT    nr_seq_interno_w
			from    paciente_atend_medic a
			where   a.nr_seq_atendimento = nr_seq_atendimento_p
			and     a.nr_seq_material = nr_seq_material_p;

			if ((nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '')
				and (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') 
				and (nr_seq_interno_w IS NOT NULL AND nr_seq_interno_w::text <> '')) then

				CALL gerar_registro_pendente_pep(
					cd_tipo_registro_p      => 'QLM',
					ie_tipo_pendencia_p     => 'A',
					nr_sequencia_registro_p	=> nr_seq_interno_w,
					cd_pessoa_fisica_p      => cd_pessoa_fisica_w,
					nr_atendimento_p        => nr_atendimento_w,
					nm_usuario_p            => nm_usuario_p,
					nr_seq_material_p       => nr_seq_material_p,
					cd_funcao_pend_p        => 3130
				);

			end if;
		end if;
	
    	EXCEPTION
    	   WHEN OTHERS THEN
          		CALL GRAVAR_LOG_TASY(7581, 
                            	SQLSTATE||chr(13)||
                            	SUBSTR(SQLERRM, 1, 200) ||chr(13)||
                            	'   nr_seq_atendimento_p: '|| nr_seq_atendimento_p ||chr(13)||
                            	'   nr_seq_material_p'     || nr_seq_material_p    ||chr(13)||
                            	'   nm_usuario_p'          || nm_usuario_p, 
                            	'AsDigital');
	end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_pend_assinatura_pep ( nr_seq_atendimento_p bigint, nr_seq_material_p bigint, nm_usuario_p text) FROM PUBLIC;

