SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_pend_atend_monit_resp (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(100);
valor_minimo_w		bigint;
valor_maximo_w		bigint;
gerar_pendencia_w	char(1);
qt_valor_w		bigint;
nr_seq_pend_regra_w	bigint;
nr_seq_pend_pac_w	bigint;
ie_respiracao_w  varchar(20);
cd_tipo_respiracao_w varchar(20);

c01 CURSOR FOR
	SELECT a.nr_sequencia,
	coalesce(a.vl_minimo,0),
	coalesce(a.vl_maximo,0),
  a.cd_tipo_respiracao
	from gqa_pendencia_regra a,
	gqa_pendencia b
	where b.nr_sequencia = a.nr_seq_pendencia
	and	b.ie_tipo_pendencia	= 12
	and	b.ie_situacao = 'A'
	and	a.ie_situacao = 'A';


BEGIN

	select	max(nr_atendimento),
	max(qt_fio2),
	max(cd_pessoa_fisica),
  max(ie_respiracao)
	into STRICT nr_atendimento_w,
	qt_valor_w,
	cd_pessoa_fisica_w,
  ie_respiracao_w
	from atendimento_monit_resp
	where nr_sequencia = nr_sequencia_p;

	open C01;
	loop
	fetch C01 into
	nr_seq_pend_regra_w,
	valor_minimo_w,
	valor_maximo_w,
  cd_tipo_respiracao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	begin

		select CASE WHEN coalesce(max(qt_valor_w)::text, '') = '' THEN  'N'  ELSE 'S' END
		into STRICT gerar_pendencia_w
		
    where qt_valor_w between valor_minimo_w and valor_maximo_w;

		--if (gerar_pendencia_w = 'S') then
    if (qt_valor_w between valor_minimo_w and valor_maximo_w) then
      if (ie_respiracao_w = cd_tipo_respiracao_w) then

			select nextval('gqa_pendencia_pac_seq')
			into STRICT nr_seq_pend_pac_w
			;

			insert into gqa_pendencia_pac(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			cd_pessoa_fisica,
			nr_atendimento,
			nr_seq_mon_resp,
			nr_seq_pend_regra)
			values (nr_seq_pend_pac_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_w,
			nr_atendimento_w,
			nr_sequencia_p,
			nr_seq_pend_regra_w);

			CALL gerar_consulta_reg_mentor(nm_usuario_p,
			'ATENDIMENTO_MONIT_RESP',
			nr_seq_pend_regra_w,
			nr_sequencia_p,
			nr_seq_pend_pac_w,
			qt_valor_w,
			'QT_FIO2');

			CALL gqa_gerar_acao_regra(nr_seq_pend_regra_w,
			nr_seq_pend_pac_w,
			nr_atendimento_w,
			cd_pessoa_fisica_w,
			nm_usuario_p);

      end if;
    end if;

	end;
	end loop;
	close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_pend_atend_monit_resp (nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

