CREATE OR REPLACE PROCEDURE gerar_pend_conta_prot_doc ( nr_seq_protocolo_p bigint, nr_seq_interno_p bigint, nr_seq_tipo_p bigint, ds_erro_p INOUT text, nm_usuario_p text) AS $body$
DECLARE

c01 CURSOR FOR 
	SELECT	distinct 
		nr_seq_interno 
	from	protocolo_doc_item 
	where	nr_sequencia 		= nr_seq_protocolo_p	 
	and (nr_seq_interno 	= nr_seq_interno_p or coalesce(nr_seq_interno_p,0) = 0);
nr_interno_conta_w 	bigint;

BEGIN
nr_interno_conta_w := 0;
open c01;
loop 
fetch c01 into nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
		if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') and (nr_seq_interno_p IS NOT NULL AND nr_seq_interno_p::text <> '') and (nr_seq_tipo_p IS NOT NULL AND nr_seq_tipo_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
			ds_erro_p := gerar_pendencia_conta(nr_interno_conta_w, nr_seq_tipo_p, nm_usuario_p, ds_erro_p);
		end if;
	end;	
end loop;
close c01;
commit;
end;
$body$
