SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_perdas_retorno ( nr_retorno_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p
	1 - gerar perdas retorno
	2 - desfazer geracao perdas retorno
*/
nr_sequencia_w	bigint;
vl_pago_w	double precision;
vl_perdas_w	double precision;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.vl_pago,
	coalesce(a.vl_perdas,0)
from	convenio_retorno_item a,
	convenio_retorno b
where	b.nr_sequencia		= nr_retorno_p
and	a.nr_seq_retorno	= b.nr_sequencia
and (ie_opcao_p <> 2 or coalesce(a.vl_perdas,0) <> 0);


BEGIN


if (ie_opcao_p = 1)	then

	open c01;
	loop
	fetch c01 into
		nr_sequencia_w,
		vl_pago_w,
		vl_perdas_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		update	convenio_retorno_item
		set	vl_pago		= 0,
			vl_perdas	= vl_pago_w,
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_w;

	end loop;
	close c01;

elsif (ie_opcao_p = 2)	then

	open c01;
	loop
	fetch c01 into
		nr_sequencia_w,
		vl_pago_w,
		vl_perdas_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		update	convenio_retorno_item
		set	vl_pago		= vl_perdas_w,
			vl_perdas	= 0,
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_w;

	end loop;
	close c01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
