SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_perda_item_unit ( nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_item_unit_p bigint, qt_material_p bigint, nr_seq_motivo_perda_p bigint, nm_computador_p text default '') AS $body$
DECLARE


--ITEM_UNITARIZACAO
qt_material_w		item_unitarizacao.qt_material%type;
nr_sequencia_w		item_unitarizacao.nr_sequencia%type;

--UNITARIZACAO
nr_seq_unitarizacao_w	unitarizacao.nr_sequencia%type;

--MATERIAL_LOTE_FORNEC
nr_seq_lote_fornec_w	material_lote_fornec.nr_sequencia%type;

--Par√¢metros
cd_local_irreg_w	funcao_parametro.vl_parametro%type;
	

BEGIN
cd_local_irreg_w := obter_valor_param_usuario(143, 313, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento);

select	qt_material,
	nr_seq_unitarizacao
into STRICT	qt_material_w,
	nr_seq_unitarizacao_w
from	item_unitarizacao
where	nr_sequencia = nr_seq_item_unit_p;

select	max(nr_sequencia)
into STRICT	nr_seq_lote_fornec_w
from	material_lote_fornec
where	nr_seq_item_unitarizacao = nr_seq_item_unit_p;

if (qt_material_p <= 0) then
	--A quantidade informada deve ser maior que zero.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(255130);
end if;

if (qt_material_p > qt_material_w) then
	--A quantidade informada deve ser menor que a quantidade do item.
	CALL wheb_mensagem_pck.exibir_mensagem_abort(254985);
end if;

if (qt_material_p = qt_material_w) then
	nr_sequencia_w := nr_seq_item_unit_p;

	update	item_unitarizacao
	set	nr_seq_motivo_perda = nr_seq_motivo_perda_p
	where	nr_sequencia = nr_seq_item_unit_p;
else
	update	item_unitarizacao
	set	qt_material = qt_material - qt_material_p
	where	nr_sequencia = nr_seq_item_unit_p;

	select	nextval('item_unitarizacao_seq')
	into STRICT	nr_sequencia_w
	;

	insert into item_unitarizacao(
		dt_atualizacao,
		dt_atualizacao_nrec,
		ie_tipo_processo,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_atividade,
		nr_seq_motivo_perda,
		nr_sequencia,
		nr_seq_unitarizacao,
		qt_material)
	SELECT	clock_timestamp(),
		clock_timestamp(),
		ie_tipo_processo,
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_atividade,
		nr_seq_motivo_perda_p,
		nr_sequencia_w,
		nr_seq_unitarizacao,
		qt_material_p
	from	item_unitarizacao
	where	nr_sequencia = nr_seq_item_unit_p;
end if;


CALL atualizar_baixa_irreg_unit(cd_estabelecimento_p,nm_usuario_p,nr_seq_unitarizacao_w,cd_local_irreg_w,nr_sequencia_w,nr_seq_lote_fornec_w, nm_computador_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
