SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_perfil_cargo ( cd_cargo_p bigint, cd_cargo_destino_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (cd_cargo_p > 0) and (cd_cargo_destino_p > 0) then
	begin
	insert into cargo_perfil(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		cd_cargo,
		cd_perfil)
	SELECT 	nextval('cargo_perfil_seq'),
		clock_timestamp(),
		nm_usuario_p,
		cd_cargo_destino_p,
		a.cd_perfil
	from	cargo_perfil a
	where	a.cd_cargo = cd_cargo_p
	and	not exists (	SELECT  1
				from	cargo_perfil x
				where 	x.cd_cargo = cd_cargo_destino_p
				and	x.cd_perfil = a.cd_perfil);


	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
