CREATE OR REPLACE PROCEDURE gerar_perfil_item_pront ( cd_perfil_p bigint, nr_seq_item_pront_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
ie_nivel_atencao_w		varchar(1);
nr_seq_apres_w			bigint := 99999;


BEGIN

Select  coalesce(max(IE_NIVEL_ATENCAO),'T')
into STRICT	ie_nivel_atencao_w
from	perfil
where   cd_perfil = cd_perfil_p;

if (ie_nivel_atencao_w = 'P' or ie_nivel_atencao_w = 'S') then

	Select  Obter_seq_apres_pront_soap(nr_seq_item_pront_p)
	into STRICT	nr_seq_apres_w
	;

end if;


select	nextval('perfil_item_pront_seq')
into STRICT	nr_sequencia_w
;

insert into perfil_item_pront(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	cd_perfil,
	nr_seq_item_pront,
	ie_atualizar,
	nr_seq_apres,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ie_checkup,
	nr_seq_apres_checkup)
values (	nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	cd_perfil_p,
	nr_seq_item_pront_p,
	'S',
	nr_seq_apres_w,
	clock_timestamp(),
	nm_usuario_p,
	'S',
	99999);

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
