CREATE OR REPLACE PROCEDURE gerar_pessoa_fisica_tws ( nm_pessoa_fisica_p pessoa_fisica.nm_pessoa_fisica%type, nr_cpf_p pessoa_fisica.nr_cpf%type, nr_identidade_p pessoa_fisica.nr_identidade%type, dt_nascimento_p text, ie_sexo_p pessoa_fisica.ie_sexo%type, nr_ddd_celular_p pessoa_fisica.nr_ddd_celular%type, nr_ddi_celular_p pessoa_fisica.nr_ddi_celular%type, nr_telefone_celular_p pessoa_fisica.nr_telefone_celular%type, nm_usuario_p wsuite_usuario.nm_usuario%type, ds_email_p compl_pessoa_fisica.ds_email%type, nr_seq_wsuite_usuario_p wsuite_usuario.nr_sequencia%type, ds_retorno_p INOUT text) AS $body$
DECLARE

			
	cd_pessoa_fisica_w pessoa_fisica.cd_pessoa_fisica%type;
	id_subject_w wsuite_usuario.id_subject%type;
	id_role_w role.id%type;


BEGIN

insert into pessoa_fisica(
	cd_pessoa_fisica, ie_tipo_pessoa, nm_pessoa_fisica,
	dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
	dt_nascimento, nr_cpf, nr_identidade,
	nr_ddd_celular, nr_ddi_celular,nr_telefone_celular,
	ie_sexo
) 	values (
	nextval('pessoa_fisica_seq'), 2, nm_pessoa_fisica_p,
	clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
	to_date(dt_nascimento_p,'dd/mm/yyyy'), nr_cpf_p, nr_identidade_p,
	nr_ddd_celular_p, nr_ddi_celular_p, nr_telefone_celular_p,
	ie_sexo_p) returning cd_pessoa_fisica into cd_pessoa_fisica_w;

if (ds_email_p IS NOT NULL AND ds_email_p::text <> '' AND cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	insert into compl_pessoa_fisica(
		cd_pessoa_fisica, nr_sequencia, ie_tipo_complemento,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,nm_usuario_nrec,
		nr_telefone, ds_email)
	values ( 
	cd_pessoa_fisica_w, 1, 1,
	clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
	nr_telefone_celular_p, ds_email_p);
end if;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	select max(id_subject)
	into STRICT id_subject_w
	from wsuite_usuario
	where nr_sequencia = nr_seq_wsuite_usuario_p;

	update wsuite_usuario
	set cd_pessoa_fisica = cd_pessoa_fisica_w,
	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
	where nr_sequencia = nr_seq_wsuite_usuario_p;

	if (id_subject_w IS NOT NULL AND id_subject_w::text <> '') then
		select max(id)
		into STRICT id_role_w
		from role
		where nm_role = 'linkedUser';

		update subject_role
		set id_role = id_role_w
		where id_subject = id_subject_w;
		commit;
	end if;
	ds_retorno_p := 'COD#'|| cd_pessoa_fisica_w;
end if;

end;
$body$
