CREATE OR REPLACE PROCEDURE gerar_pf_escala_adic_equipe ( nr_seq_escala_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
nr_seq_equipe_w		bigint;

C01 CURSOR FOR
	SELECT	distinct
		cd_pessoa_fisica
	from	pf_equipe_partic
	where	nr_seq_equipe = nr_seq_equipe_w;


BEGIN

if (coalesce(nr_seq_escala_p,0) > 0) then
	begin
	select	max(nr_seq_equipe)
	into STRICT	nr_seq_equipe_w
	from	escala_diaria
	where	nr_sequencia = nr_seq_escala_p;

	open C01;
	loop
	fetch C01 into
		cd_pessoa_fisica_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert into escala_diaria_adic(
				nr_sequencia,
				nr_seq_escala_diaria,
				dt_atualizacao,
				nm_usuario,
				cd_pessoa_fisica,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_classif_escala,
				qt_min_executado,
				nr_ordem_escala,
				ds_observacao)
			values (	nextval('escala_diaria_adic_seq'),
				nr_seq_escala_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_pessoa_fisica_w,
				clock_timestamp(),
				nm_usuario_p,
				null,
				null,
				null,
				null);
		end;
	end loop;
	close C01;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
