CREATE OR REPLACE PROCEDURE gerar_pf_usuarios_terceiro (nr_seq_terceiro_p bigint, cd_perfil_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(255);

C01 CURSOR FOR
	SELECT	a.cd_pessoa_fisica
	from	usuario a
	where	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '')
	and	a.ie_situacao = 'A'
	and	not exists (
			SELECT	1
			from	terceiro_pessoa_consulta x
			where	x.nr_seq_terceiro	= nr_seq_terceiro_p
			and	x.cd_perfil = cd_perfil_p
			and	x.cd_pessoa_fisica = a.cd_pessoa_fisica)
	order by a.cd_pessoa_fisica;


BEGIN

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into terceiro_pessoa_consulta(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_terceiro,
		cd_pessoa_fisica,
		cd_perfil)
	values (	nextval('terceiro_pessoa_consulta_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_terceiro_p,
		cd_pessoa_fisica_w,
		cd_perfil_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
