CREATE OR REPLACE PROCEDURE gerar_pix_cobranca (cd_cobranca_pix_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_transmissao_w bigint;
ds_data_w varchar(2000);
ds_result_w varchar(2000);


BEGIN

    select nextval('pix_transmissao_seq')
    into STRICT nr_seq_transmissao_w
;

    insert into pix_transmissao(
        nr_sequencia,
        nr_seq_pix_cob,
        dt_transmissao,
        nm_usuario,
        ie_tipo_transmissao)
    values (
        nr_seq_transmissao_w,
        cd_cobranca_pix_p,
        clock_timestamp(),
        nm_usuario_p,
        'R'
    );

    ds_data_w := '{ "pixBillingId" : ' || cd_cobranca_pix_p || ',' ||
                   ' "pixTransmissionId" : ' || nr_seq_transmissao_w || ', ' || 
                   ' "userName" : "' || nm_usuario_p || '" ' ||
                   '}';

    ds_result_w := bifrost.send_integration(
    'pix.registrar.cobranca.imediata',
    'com.philips.tasy.integration.pix.api.billing.inbound.callback.RequestBillingRegistrationCallback',
    ds_data_w , nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
