CREATE OR REPLACE PROCEDURE gerar_plano_medic ( nr_seq_versao_p bigint, ds_descricao_complementar_p text, ie_classificacao_p bigint, cd_material_p bigint, qt_dose_manha_p bigint, qt_dose_almoco_p bigint, qt_dose_tarde_p bigint, qt_dose_noite_p bigint, cd_unidade_medida_plano_p text, ds_observacao_p text, ds_motivo_p text, ds_horario_esp_p text, cd_pessoa_fisica_p text, nr_crm_p text, ds_orientacao_p text, ds_medicamento_p text, ds_forma_medicamento_p text, ds_potencia_p text, cd_profissional_p text, ds_forma_desc_p text, ds_unid_med_desc_p text, ds_princ_ativo_p text, ds_dose_diferenciada_p text) AS $body$
DECLARE

    	ds_medicamento_w   		plano_versao_medic.ds_medicamento%TYPE;
    	ie_tipo_medicamento_w 	plano_versao_medic.ds_medicamento%TYPE;
		nr_sequencia_w			plano_versao_medic.nr_sequencia%type;
		ie_exibir_plano_medic_w varchar(1);
		


BEGIN

	if (coalesce(cd_material_p::text, '') = '') then
        ds_medicamento_w := ds_medicamento_p;
		ie_tipo_medicamento_w :=  'N';
	else
       	ds_medicamento_w := null;
		ie_tipo_medicamento_w := 'S';
    end if;
	
	if (coalesce(ds_medicamento_p::text, '') = '' and coalesce(cd_material_p::text, '') = '') then
		ie_tipo_medicamento_w := 'O';
	end if;
	
	select	nextval('plano_versao_medic_seq')
	into STRICT	nr_sequencia_w
	;
	
	ie_exibir_plano_medic_w := obter_se_exibe_plano_medic(ds_descricao_complementar_p,
														  ie_classificacao_p,
														  cd_material_p,
														  qt_dose_manha_p,
														  qt_dose_almoco_p,
														  qt_dose_tarde_p,
														  qt_dose_noite_p,
														  cd_unidade_medida_plano_p,
														  ds_observacao_p,
														  ds_motivo_p,
														  ds_horario_esp_p,
														  cd_pessoa_fisica_p,
														  ds_orientacao_p,
														  ds_medicamento_w,
														  ds_forma_medicamento_p,
														  ds_dose_diferenciada_p,
														  ds_potencia_p,
														  ds_forma_desc_p,
														  ds_unid_med_desc_p,
														  ds_princ_ativo_p);
														
    insert into plano_versao_medic( nr_sequencia,
                   	nr_seq_versao,
                   	ds_descricao_complementar,
					ie_classificacao,
                   	cd_material,
                   	qt_dose_manha,
                   	qt_dose_almoco,
                   	qt_dose_tarde,
                   	qt_dose_noite,
                   	cd_unidade_medida,
                   	ds_observacao,
                   	ds_motivo,
                   	ds_horario_especial,
                   	dt_registro,
                   	nm_usuario,
                   	dt_atualizacao,
                   	nm_usuario_nrec,
                   	dt_atualizacao_nrec,
                   	cd_pessoa_fisica,
                   	cd_profissional,
                   	ds_orientacao,
                   	ds_medicamento,
                   	ds_forma_medicamento,
                   	ds_potencia,
					ds_dose_diferenciada,
					ds_forma_desc,
					ds_unid_med_desc,
					ie_tipo_medicamento,
					ds_princ_ativo,
					ie_exibir_plano_medic)
    values ( 	nr_sequencia_w,
             		nr_seq_versao_p,
             		ds_descricao_complementar_p,
            		ie_classificacao_p,
             		cd_material_p,
             		qt_dose_manha_p,
             		qt_dose_almoco_p,
             		qt_dose_tarde_p,
             		qt_dose_noite_p,
             		cd_unidade_medida_plano_p,
             		ds_observacao_p,
             		ds_motivo_p,
             		ds_horario_esp_p,
             		clock_timestamp(),
             		obter_usuario_ativo(),
             		clock_timestamp(),
				obter_usuario_ativo(),
             		clock_timestamp(),
             		cd_pessoa_fisica_p,
             		cd_profissional_p,
             		ds_orientacao_p,
             		ds_medicamento_w,
             		ds_forma_medicamento_p,
             		ds_potencia_p,
				ds_dose_diferenciada_p,
				ds_forma_desc_p,
				ds_unid_med_desc_p,
				ie_tipo_medicamento_w,
				ds_princ_ativo_p,
				ie_exibir_plano_medic_w);
	
	delete	from w_medic_plano_imp
	where	nm_usuario = obter_usuario_ativo();
	
	if (ie_exibir_plano_medic_w <> 'N') then
		CALL gerar_medic_uso(null, nr_sequencia_w);
	end if;
	
	commit;

end;
$body$
