CREATE OR REPLACE PROCEDURE gerar_plano_testes_migracao ( nr_seq_gerencia_p bigint, nr_seq_grupo_desenv_p bigint, ie_excl_reg_antigos_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_gerencia_w	bigint;
nr_seq_grupo_desenv_w	bigint;
nr_seq_projeto_w	bigint;
nr_seq_plan_teste_w	bigint;
nr_seq_plan_proj_w	bigint;

c01 CURSOR FOR
SELECT	g.nr_seq_gerencia,
	g.nr_sequencia,
	p.nr_sequencia
from	funcao_grupo_des f,
	grupo_desenvolvimento g,
	proj_projeto p
where	f.nr_seq_grupo = g.nr_sequencia
and	f.cd_funcao = p.cd_funcao
and	p.nr_seq_gerencia = 9
and	p.nr_seq_classif = 14
and	g.nr_seq_gerencia = nr_seq_gerencia_p
and	g.nr_sequencia = coalesce(nr_seq_grupo_desenv_p,g.nr_sequencia)
and	p.nr_seq_estagio not in (21,26)
and	not exists (
		SELECT	1
		from	w_plan_teste_migr x,
			w_plan_teste_migr_proj y
		where	x.nr_sequencia = y.nr_seq_plan_teste
		and	x.nr_seq_gerencia = g.nr_seq_gerencia
		and	x.nr_seq_grupo_desenv = g.nr_sequencia
		and	y.nr_seq_projeto = p.nr_sequencia)

order by
	g.nr_seq_gerencia,
	g.nr_sequencia,
	p.nr_sequencia;


BEGIN
if (nr_seq_gerencia_p IS NOT NULL AND nr_seq_gerencia_p::text <> '') and (ie_excl_reg_antigos_p IS NOT NULL AND ie_excl_reg_antigos_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	if (ie_excl_reg_antigos_p = 'S') then
		begin
		delete
		from	w_plan_teste_migr_proj
		where	nr_seq_plan_teste in (
				SELECT	nr_sequencia
				from	w_plan_teste_migr
				where	nr_seq_gerencia = nr_seq_gerencia_p
				and	nr_seq_grupo_desenv = coalesce(nr_seq_grupo_desenv_p,nr_seq_grupo_desenv));

		delete
		from	w_plan_teste_migr
		where	nr_seq_gerencia = nr_seq_gerencia_p
		and	nr_seq_grupo_desenv = coalesce(nr_seq_grupo_desenv_p,nr_seq_grupo_desenv);
		end;
	end if;

	open c01;
	loop
	fetch c01 into	nr_seq_gerencia_w,
			nr_seq_grupo_desenv_w,
			nr_seq_projeto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	coalesce(max(nr_sequencia),0)
		into STRICT	nr_seq_plan_teste_w
		from	w_plan_teste_migr
		where	nr_seq_gerencia = nr_seq_gerencia_w
		and	nr_seq_grupo_desenv = nr_seq_grupo_desenv_w;

		if (nr_seq_plan_teste_w = 0) then
			begin
			select	nextval('w_plan_teste_migr_seq')
			into STRICT	nr_seq_plan_teste_w
			;

			insert into w_plan_teste_migr(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_gerencia,
				nr_seq_grupo_desenv,
				dt_aprovacao)
			values (
				nr_seq_plan_teste_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_gerencia_w,
				nr_seq_grupo_desenv_w,
				null);
			end;
		end if;

		select	coalesce(max(y.nr_sequencia),0)
		into STRICT	nr_seq_plan_proj_w
		from	w_plan_teste_migr x,
			w_plan_teste_migr_proj y
		where	x.nr_sequencia = y.nr_seq_plan_teste
		and	x.nr_seq_gerencia = nr_seq_gerencia_w
		and	x.nr_seq_grupo_desenv = nr_seq_grupo_desenv_w
		and	y.nr_seq_projeto = nr_seq_projeto_w;

		if (nr_seq_plan_proj_w = 0) then
			begin
			select	nextval('w_plan_teste_migr_proj_seq')
			into STRICT	nr_seq_plan_proj_w
			;

			insert into w_plan_teste_migr_proj(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_plan_teste,
				nr_seq_projeto,
				dt_plan_inicio_teste,
				dt_plan_termino_teste,
				ie_status,
				ie_teste_anal_migr,
				ie_revisao,
				ie_dependencia_desenv,
				ie_dependencia_tecnologia)
			values (
				nr_seq_plan_proj_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_plan_teste_w,
				nr_seq_projeto_w,
				null,
				null,
				'P',
				'N',
				'S',
				'N',
				'N');
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_plano_testes_migracao ( nr_seq_gerencia_p bigint, nr_seq_grupo_desenv_p bigint, ie_excl_reg_antigos_p text, nm_usuario_p text) FROM PUBLIC;

