CREATE OR REPLACE PROCEDURE gerar_pontuacao_aldrete ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_pontuacao_w	smallint;


BEGIN


select	sum(	ie_atividade		+
		ie_respiracao		+
		ie_pressao_arterial	+
		ie_consciencia		+
		ie_coloracao )
into STRICT	qt_pontuacao_w
from	escala_aldrete
where	nr_sequencia	=	nr_sequencia_p;

update	escala_aldrete
set	qt_aldrete	=	qt_pontuacao_w,
	nm_usuario	=	nm_usuario_p,
	dt_atualizacao	=	clock_timestamp()
where	nr_sequencia	=	nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
