CREATE OR REPLACE PROCEDURE gerar_pontuacao_chung ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_pontuacao_w	smallint;


BEGIN


select	sum(	ie_sinais_vitais+
		ie_atividade	+
		ie_nauseas	+
		ie_dor		+
		ie_sangramento )
into STRICT	qt_pontuacao_w
from	escala_chung
where	nr_sequencia	=	nr_sequencia_p;

update	escala_chung
set	qt_chung	=	qt_pontuacao_w,
	nm_usuario	=	nm_usuario_p,
	dt_atualizacao	=	clock_timestamp()
where	nr_sequencia	=	nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
