CREATE OR REPLACE PROCEDURE gerar_posicao_cirurgia_pepo ( nr_cirurgia_p bigint, cd_profissional_p text, ie_posicao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN

select	nextval('posicao_cirurgia_seq')
into STRICT	nr_sequencia_w
;

if (nr_cirurgia_p > 0 and (cd_profissional_p IS NOT NULL AND cd_profissional_p::text <> '')) then

	insert into posicao_cirurgia(	nr_sequencia,
			nr_cirurgia,
			ie_posicao,
			cd_profissional,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_posicao,
			ie_situacao)
	values (nr_sequencia_w,
			nr_cirurgia_p,
			ie_posicao_p,
			cd_profissional_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			'A');
	commit;

end if;

end;
