CREATE OR REPLACE PROCEDURE gerar_preco_atual_mat ( cd_convenio_p bigint, cd_tab_preco_mat_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_material_w			bigint;
dt_inicio_vigencia_w		timestamp;
vl_material_w			double precision;

c01 CURSOR FOR
SELECT	nr_sequencia,
	cd_material
from	convenio_carga_preco
where	cd_convenio = cd_convenio_p
and	(cd_material IS NOT NULL AND cd_material::text <> '');


BEGIN
open c01;
loop
fetch c01 into
	nr_sequencia_w,
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	max(dt_inicio_vigencia)
	into STRICT	dt_inicio_vigencia_w
	from	preco_material
	where	cd_material 		= cd_material_w
	and	cd_tab_preco_mat 	= cd_tab_preco_mat_p
	and	cd_estabelecimento 	= cd_estabelecimento_p;

	select	coalesce(max(vl_preco_venda),0)
	into STRICT	vl_material_w
	from	preco_material
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_material		= cd_material_w
	and	dt_inicio_vigencia	= dt_inicio_vigencia_w
	and	cd_tab_preco_mat	= cd_tab_preco_mat_p
	and	(dt_inicio_vigencia_w IS NOT NULL AND dt_inicio_vigencia_w::text <> '');


	update	convenio_carga_preco
	set	vl_atual		= vl_material_w,
		ie_status		= 'A',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia		= nr_sequencia_w;

end loop;
close c01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
