CREATE OR REPLACE PROCEDURE gerar_preco_ultima_cotacao ( nr_cot_compra_p bigint, cd_cgc_fornecedor_p text, cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE



nr_cot_compra_w			bigint;
cd_estabelecimento_w		smallint;
cd_material_w			integer;
vl_unitario_material_w		double precision;
nr_sequencia_w			bigint;

C01 CURSOR FOR
SELECT	a.cd_estabelecimento,
	d.nr_sequencia,
	d.cd_material
from	cot_compra a,
	cot_compra_item b,
	cot_compra_forn c,
	cot_compra_forn_item d
where	a.nr_cot_compra = b.nr_cot_compra
and	b.nr_cot_compra = d.nr_cot_compra
and	b.nr_item_cot_compra = d.nr_item_cot_compra
and	c.nr_sequencia = d.nr_seq_cot_forn
and	a.nr_cot_compra = nr_cot_compra_p
and (c.cd_cgc_fornecedor = cd_cgc_fornecedor_p or c.cd_pessoa_fisica = cd_pessoa_fisica_p)
and	(d.cd_material IS NOT NULL AND d.cd_material::text <> '')
and	coalesce(b.ie_situacao,'A') = 'A';


BEGIN

open C01;
loop
fetch C01 into
	cd_estabelecimento_w,
	nr_sequencia_w,
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(b.nr_cot_compra),0)
	into STRICT	nr_cot_compra_w
	from	cot_compra a,
		cot_compra_forn b,
		cot_compra_forn_item c
	where	a.nr_cot_compra = b.nr_cot_compra
	and	b.nr_sequencia = c.nr_seq_cot_forn
	and	a.cd_estabelecimento = cd_estabelecimento_w
	and (b.cd_cgc_fornecedor = cd_cgc_fornecedor_p or b.cd_pessoa_fisica = cd_pessoa_fisica_p)
	and	c.cd_material = cd_material_w
	and	a.nr_cot_compra <> nr_cot_compra_p
	and	c.vl_unitario_material > 0
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = '';

	if (nr_cot_compra_w > 0) then

		select	coalesce(min(b.vl_unitario_material),0)
		into STRICT	vl_unitario_material_w
		from	cot_compra_forn a,
			cot_compra_forn_item b
		where	a.nr_sequencia = b.nr_seq_cot_forn
		and (a.cd_cgc_fornecedor = cd_cgc_fornecedor_p or a.cd_pessoa_fisica = cd_pessoa_fisica_p)
		and	b.cd_material = cd_material_w
		and	b.nr_cot_compra = nr_cot_compra_w;

		update	cot_compra_forn_item
		set	vl_unitario_material	= vl_unitario_material_w,
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_w;

	end if;
	end;
end loop;
close C01;

CALL gerar_hist_cotacao_sem_commit(
	nr_cot_compra_p,
	WHEB_MENSAGEM_PCK.get_texto(297639),
	substr(WHEB_MENSAGEM_PCK.get_texto(301920),1,4000),
	'S',
	nm_usuario_p);

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
