SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_preparo_processo_adep ( nr_seq_processo_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_nao_lido_w	bigint;


BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	count(*)
	into STRICT	qt_nao_lido_w
	from	adep_processo_item
	where	nr_seq_processo	= nr_seq_processo_p
	and		ie_barras		= 'N'
	and		ie_suspenso		= 'N';

	if (qt_nao_lido_w = 0) then
		update	adep_processo
		set		dt_preparo		= clock_timestamp(),
				nm_usuario_preparo	= nm_usuario_p
		where	nr_sequencia		= nr_seq_processo_p;
	else
		---Existem itens pendentes (não lidos por barras) no processo.Não é possível concluir o preparo sem todos os itens necessários.
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(261466);
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
