SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prescricao_banco_sangue (nr_seq_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_prescricao_p INOUT bigint) AS $body$
DECLARE

 
nr_prescricao_w			bigint 	:= null;
cd_setor_atendimento_w		integer	:= 0;
ie_origem_inf_w			varchar(1);
ie_adep_w		  	varchar(10);
ie_forma_adep_w		  	varchar(10);

 

BEGIN 
if (coalesce(nr_seq_agenda_p,0) > 0) then 
	ie_forma_adep_w := Obter_Param_Usuario(924, 246, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_forma_adep_w);
 
	select	coalesce(max(cd_setor_exclusivo),0) 
	into STRICT	cd_setor_atendimento_w 
	from	Agenda b, 
		agenda_paciente a 
	where 	a.cd_agenda	= b.cd_agenda 
	 and	a.nr_sequencia	= nr_seq_agenda_p;
 
	if (cd_setor_atendimento_w = 0) then 
		select	cd_setor_atendimento 
		into STRICT	cd_setor_atendimento_w 
		from 	usuario 
		where 	nm_usuario = nm_usuario_p;
	end if;
 
 
	select 	coalesce(max('1'),'3') 
	into STRICT	ie_origem_inf_w 
	from	Medico b, 
		Usuario a 
	where 	a.nm_usuario		= nm_usuario_p 
	 and	a.cd_pessoa_fisica	= b.cd_pessoa_fisica;
 
	Select 	nextval('prescr_medica_seq')  
	into STRICT 	nr_prescricao_w 
	;
 
	if (ie_forma_adep_w = 'DS') then 
		select	coalesce(max(ie_adep),'N') 
		into STRICT	ie_adep_w 
		from	setor_atendimento 
		where	cd_setor_atendimento = cd_setor_atendimento_w;
	elsif (ie_forma_adep_w = 'NV') then 
		ie_adep_w  := 'N';
	elsif (ie_forma_adep_w = 'PV') then 
		ie_adep_w := 'S';
	elsif (ie_forma_adep_w = 'PNV') then 
		ie_adep_w := 'N';
	else	 
		ie_adep_w := 'S';
	end if;
 
	begin 
	insert into prescr_medica( 
			nr_prescricao      ,    
			cd_pessoa_fisica     ,    
			nr_atendimento      ,    
			cd_medico        ,    
			dt_prescricao      ,    
			dt_atualizacao      ,    
			nm_usuario        , 
			nm_usuario_original	 ,    
			nr_horas_validade    ,    
			dt_primeiro_horario   ,    
			dt_liberacao       ,    
			cd_setor_atendimento, 
			cd_setor_entrega, 
			dt_entrega, 
			ie_origem_inf, 
			nr_seq_agenda, 
			ie_recem_nato, 
			cd_estabelecimento, 
			cd_prescritor, 
			ie_adep) 
		SELECT 	nr_prescricao_w, 
			a.cd_pessoa_fisica, 
			a.nr_atendimento, 
			a.cd_medico, 
			to_date(to_char(a.dt_agenda,'dd/mm/yyyy') || ' ' || 
				to_char(hr_inicio,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss'), 
			clock_timestamp(), 
			nm_usuario_p, 
			nm_usuario_p,		 
			24, 
			a.hr_inicio,  
			null, 
			cd_setor_atendimento_w, 
			cd_setor_atendimento_w, 
			null, 
			ie_origem_inf_w, 
			null, 
			'N', 
			cd_estabelecimento_p, 
			obter_dados_usuario_opcao(nm_usuario_p, 'C'), 
			ie_adep_w 
		from	agenda_paciente a 
		where	a.nr_sequencia = nr_seq_agenda_p;
				 
	exception 
		when others then 
		nr_prescricao_w := null;
	end;	
end if;	
	 
if (coalesce(nr_prescricao_w,0) > 0) then 
	nr_prescricao_p := nr_prescricao_w;
end if;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
