CREATE OR REPLACE PROCEDURE gerar_prescricao_curativo_js ( nr_sequencia_p bigint, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

		 
ds_erro_w	varchar(2000);
nr_prescricao_w	bigint;
ie_liberar_w	varchar(255);


BEGIN 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin	 
	 
	CALL gerar_prescricao_curativo(nr_sequencia_p, nm_usuario_p);
	 
	select	max(nr_prescricao) 
	into STRICT	nr_prescricao_w 
	from	cur_curativo 
	where	nr_sequencia = nr_sequencia_p;
	 
	ie_liberar_w := Obter_Param_Usuario(281, 658, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_liberar_w);
	 
	if (ie_liberar_w = 'S') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then 
		begin 
		 
		CALL Gerar_kit_Prescricao(	obter_estabelecimento_ativo, 
					nr_prescricao_w, 
					null, 
					nm_usuario_p, 
					'N');
		 
		ds_erro_w := liberar_prescricao( 
			nr_prescricao_w, nr_atendimento_p, obter_se_usuario_medico(nm_usuario_p), obter_perfil_ativo, nm_usuario_p, 'S', ds_erro_w);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
