CREATE OR REPLACE PROCEDURE gerar_prescricao_dispositivo ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_medico_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_prescricao_p INOUT bigint) AS $body$
DECLARE

 
nr_prescricao_w			bigint;
cd_medico_atendimento_w		varchar(10);


BEGIN 
 
select	nextval('prescr_medica_seq') 
into STRICT	nr_prescricao_w
;
 
select	cd_medico_atendimento 
into STRICT	cd_medico_atendimento_w 
from	atendimento_paciente 
where	nr_atendimento	=	nr_atendimento_p;
 
insert into prescr_medica( 
	nr_prescricao, 
	cd_pessoa_fisica, 
	nr_atendimento, 
	cd_medico, 
	dt_prescricao, 
	dt_atualizacao, 
	nm_usuario, 
	cd_estabelecimento, 
	cd_prescritor, 
	ie_origem_inf, 
	nm_usuario_original) 
values (	nr_prescricao_w, 
	cd_pessoa_fisica_p, 
	nr_atendimento_p, 
	CASE WHEN coalesce(cd_medico_p::text, '') = '' THEN cd_medico_atendimento_w  ELSE cd_medico_p END , 
	clock_timestamp(), 
	clock_timestamp(), 
	nm_usuario_p, 
	cd_estabelecimento_p, 
	Obter_Dados_Usuario_Opcao(nm_usuario_p,'C'), 
	'N', 
	nm_usuario_p);
commit;
 
nr_prescricao_p	:= nr_prescricao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
