SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prescricao_mentor ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, nr_seq_protocolo_p bigint, nm_usuario_p text, nr_seq_pend_pac_acao_p bigint, nr_prescricao_p INOUT bigint) AS $body$
DECLARE

 
cd_pessoa_fisica_w		varchar(50);
cd_protocolo_w			bigint;
nr_seq_protocolo_w		bigint;
nm_usuario_w      varchar(255);
cd_pessoa_fisica_dest_w	varchar(10);		
dt_prim_hor_prescr_w	timestamp;
ie_estender_validade_w	varchar(5);
nr_horas_validade_w		integer;
nr_prescricao_w			bigint := 0;

BEGIN
 
ie_estender_validade_w := obter_param_usuario(924, 249, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_estender_validade_w);
 
 
if (coalesce(nr_atendimento_p,0) > 0) then 
		select	max(cd_pessoa_fisica) 
		into STRICT	cd_pessoa_fisica_w 
		from	atendimento_paciente 
		where	nr_atendimento	= nr_atendimento_p;
	else 
		cd_pessoa_fisica_w := cd_pessoa_fisica_p;
	end if;
	 
	select	max(nr_sequencia), 
			max(cd_protocolo) 
	into STRICT	nr_seq_protocolo_w, 
			cd_protocolo_w 
	from  protocolo_medicacao 
	where  ie_situacao = 'A' 
	and		nr_seq_interna = nr_seq_protocolo_p;
	 
	if (obter_se_usuario_medico(nm_usuario_p) = 'S') then 
	 
		 
		cd_pessoa_fisica_dest_w := Obter_Pf_Usuario(nm_usuario_p,'C');
	 
	else 
	 
	 
		select	max(cd_medico_resp) 
		into STRICT	cd_pessoa_fisica_dest_w	 
		from	atendimento_paciente 
		where	nr_atendimento	= nr_atendimento_p;
		 
		 
		select coalesce(max(nm_usuario), '') 
		into STRICT	nm_usuario_w	 
		from	usuario 
		where	cd_pessoa_fisica = cd_pessoa_fisica_dest_w;
		 
	end if;
	 
	dt_prim_hor_prescr_w := to_date('30/12/1899 ' || to_char(clock_timestamp(), 'hh24:mi') || ':00','dd/mm/yyyy hh24:mi:ss');
	 
	select	obter_horas_validade_prescr(dt_prim_hor_prescr_w,nr_atendimento_p,ie_estender_validade_w,'A',clock_timestamp(), null) 
	into STRICT	nr_horas_validade_w 
	;
	 
	nr_prescricao_w := Gerar_Prescricao_Protocolo(	cd_pessoa_fisica_w, cd_pessoa_fisica_dest_w, nr_atendimento_p, cd_protocolo_w, nr_seq_protocolo_w, 0, 0, nr_horas_validade_w, nr_prescricao_w, null, null, to_char(clock_timestamp(),'hh24:mi'), clock_timestamp(), null, 1, 'N', coalesce(nm_usuario_w, nm_usuario_p), null, null, 'N', nr_seq_pend_pac_acao_p);
								 
	nr_prescricao_p := coalesce(nr_prescricao_w, 0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
