CREATE OR REPLACE PROCEDURE gerar_prescricao_protocolo_js ( nr_atendimento_p bigint, nr_prescricao_p INOUT bigint, ie_atendimento_p text, ie_sem_atendimento_p text, cd_pessoa_fisica_p text, cd_medico_p text, cd_protocolo_p bigint, nr_sequencia_p bigint, qt_superf_corp_p bigint, qt_peso_p bigint, nr_horas_validade_p bigint, dt_procedimento_p timestamp, ds_dados_clinicos_p text, dt_primeiro_horario_p text, dt_prescricao_p timestamp, cd_intervalo_p text, qt_protocolo_p bigint, ie_adep_p text, nm_computador_p text, dt_prim_horario_itens_p text, ie_urgencia_p text, ds_observacao_p text, nr_seq_pend_pac_acao_p bigint default null, nm_usuario_p text DEFAULT NULL) AS $body$
DECLARE


nr_atendimento_w	bigint;
ie_fim_conta_w		varchar(1);
ie_atendimento_w	varchar(1);
nr_prescricao_w		bigint;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	
	nr_prescricao_w		:= nr_prescricao_p;
	ie_atendimento_w	:= ie_atendimento_p;
	
	if (nr_atendimento_p <> 0) and (ie_atendimento_w = 'S') then
		begin
		
		select	coalesce(ie_fim_conta,'N')
		into STRICT	ie_fim_conta_w
		from	atendimento_paciente
		where	nr_atendimento	= nr_atendimento_p;
		
		if (ie_fim_conta_w <> 'F') then
			begin
			nr_atendimento_w := nr_atendimento_p;
			end;
		elsif (ie_sem_atendimento_p = 'S') then
			begin
			nr_atendimento_w 	:= null;
			ie_atendimento_w	:= 'N';
			end;
		else
			begin
			CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(57007);
			--Ra ise_application_error(-20011,obter_texto_tasy(81335,wheb_usuario_pck.get_nr_seq_idioma) || '#@#@');
			end;
		end if;
		end;
	elsif (ie_sem_atendimento_p = 'S') then
		begin
		nr_atendimento_w	:= null;
		end;
	else
		begin
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(81335);
		--Ra ise_application_error(-20011,obter_texto_tasy(81335,wheb_usuario_pck.get_nr_seq_idioma) || '#@#@');
		end;
	end if;
	
	nr_prescricao_w := gerar_prescricao_protocolo(
		cd_pessoa_fisica_p, cd_medico_p, nr_atendimento_w, cd_protocolo_p, nr_sequencia_p, qt_superf_corp_p, qt_peso_p, nr_horas_validade_p, nr_prescricao_w, dt_procedimento_p, ds_dados_clinicos_p, dt_primeiro_horario_p, dt_prescricao_p, cd_intervalo_p, qt_protocolo_p, ie_adep_p, nm_usuario_p, nm_computador_p, dt_prim_horario_itens_p, ie_urgencia_p, nr_seq_pend_pac_acao_p, ds_observacao_p);		
	end;
end if;
commit;
nr_prescricao_p	:= nr_prescricao_w;
end;
$body$
