CREATE OR REPLACE PROCEDURE gerar_prescricao_rotina_todas ( nr_atendimento_p bigint, nr_sequencias_p text, ie_tipo_item_p text, cd_pessoa_fisica_p text, dt_primeiro_horario_p text, dt_prescricao_p timestamp, cd_medico_p text, nr_horas_validade_p bigint, cd_estabelecimento_p bigint, cd_intervalo_p text, qt_protocolo_p bigint, ie_recem_nato_p text, cd_rn_p text, nr_prescricao_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_rotinas_w	varchar(255);
nr_pos_virgula_w	bigint;	
nr_seq_rotina_w		bigint;
nr_pos_hifem_w		bigint	:= 0;
nr_sequencia_w		bigint;	
nr_prescricao_w		bigint;	
ie_tipo_item_w		varchar(15);
cd_intervalo_w		varchar(7);
aux_w			varchar(255);


BEGIN 
 
if (nr_sequencias_p IS NOT NULL AND nr_sequencias_p::text <> '') then 
	begin 
	nr_seq_rotinas_w	:= nr_sequencias_p;
	while(nr_seq_rotinas_w IS NOT NULL AND nr_seq_rotinas_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in nr_seq_rotinas_w);
		if (nr_pos_virgula_w > 0) then 
			begin 
			aux_w			:= (substr(nr_seq_rotinas_w,1,nr_pos_virgula_w-1));
			nr_pos_hifem_w		:= position('-' in aux_w);
			nr_sequencia_w		:= (substr(aux_w,1,nr_pos_hifem_w-1))::numeric;
			cd_intervalo_w		:= (substr(aux_w,nr_pos_hifem_w+1, length(aux_w)));			
			nr_seq_rotinas_w	:= substr(nr_seq_rotinas_w,nr_pos_virgula_w+1,length(nr_seq_rotinas_w));
			if (nr_sequencia_w > 0) then 
				begin 
				select CASE WHEN ie_tipo_item='P' THEN  nr_seq_protocolo WHEN ie_tipo_item='REE' THEN  nr_seq_rotina_exame WHEN ie_tipo_item='ER' THEN  nr_seq_exame_lab_rot WHEN ie_tipo_item='PR' THEN  nr_seq_proc_rotina WHEN ie_tipo_item='SR' THEN  nr_seq_sol_rotina WHEN ie_tipo_item='GAS' THEN  nr_seq_gas WHEN ie_tipo_item='REC' THEN  nr_sequencia WHEN ie_tipo_item='MER' THEN  nr_sequencia END  nr_seq_rotina, 
					ie_tipo_item 
				into STRICT	nr_seq_rotina_w, 
					ie_tipo_item_w 
				from  rep_rotina_item 
				where	nr_sequencia	= nr_sequencia_w;
	 
				nr_prescricao_w := Gerar_Prescricao_Rotina(nr_atendimento_p, nr_seq_rotina_w, ie_tipo_item_w, cd_pessoa_fisica_p, dt_primeiro_horario_p, dt_prescricao_p, cd_medico_p, nr_horas_validade_p, cd_estabelecimento_p, cd_intervalo_w, qt_protocolo_p, nr_prescricao_w, nm_usuario_p, ie_recem_nato_p, cd_rn_p, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null);
 
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;
nr_prescricao_p := nr_prescricao_w;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
