							ds_item_atual_w	:= null;
							end;
						end if;
						 
						if (ds_item_atual_w IS NOT NULL AND ds_item_atual_w::text <> '') then 
							begin 
							nr_pos_separador_w	:= position('-' in ds_item_atual_w);
						 
							if (nr_pos_separador_w > 0) then 
