CREATE OR REPLACE PROCEDURE gerar_prescr_equip_agenda (nr_seq_agenda_p bigint, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_equipamento_w	integer;
nr_sequencia_w		bigint;

c01 CURSOR FOR
	SELECT	cd_equipamento
	from	agenda_pac_equip
	where	nr_seq_agenda	= nr_seq_agenda_p
	and	(dt_confirmacao IS NOT NULL AND dt_confirmacao::text <> '');


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then
	begin

	open	c01;
	loop
	fetch	c01 into
		cd_equipamento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select	coalesce(max(nr_sequencia),0) + 1
		into STRICT	nr_sequencia_w
		from	prescr_equipamento
		where	nr_prescricao	= nr_prescricao_p;

		insert	into prescr_equipamento(nr_prescricao,
			nr_sequencia,
			cd_equipamento,
			dt_atualizacao,
			nm_usuario)
		values (nr_prescricao_p,
			nr_sequencia_w,
			cd_equipamento_w,
			clock_timestamp(),
			nm_usuario_p);

		end;
	end loop;
	close	c01;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
