SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prescr_fanep (cd_estabelecimento_p bigint, nr_seq_pepo_p bigint, nm_usuario_p text, nr_prescricao_p INOUT bigint) AS $body$
DECLARE


nr_prescricao_w		bigint;
cd_setor_atendimento_w	bigint;
dt_inicio_w		timestamp;
cd_medico_anestesista_w	varchar(10);
cd_pessoa_fisica_w	varchar(10);
ie_tipo_pessoa_w	smallint;
ie_forma_adep_w		varchar(10);
ie_adep_w		varchar(10);
nr_atendimento_w	bigint;
cd_setor_atend_prescr_pepo_w bigint;


BEGIN
select	coalesce(max(nr_prescricao), 0)
into STRICT	nr_prescricao_w
from	pepo_cirurgia
where	nr_sequencia = nr_seq_pepo_p;

if (nr_prescricao_w = 0) then
	begin
	select	dt_inicio_proced,
		cd_medico_anestesista,
		cd_pessoa_fisica,
		nr_atendimento,
		cd_setor_atend_prescr
	into STRICT	dt_inicio_w,
		cd_medico_anestesista_w,
		cd_pessoa_fisica_w,
		nr_atendimento_w,
		cd_setor_atend_prescr_pepo_w
	from	pepo_cirurgia
	where	nr_sequencia = nr_seq_pepo_p;

	/*
	select	nvl(max(b.ie_tipo_pessoa),'1')
	into	ie_tipo_pessoa_w
	from	pessoa_fisica b,
		usuario a
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	a.nm_usuario	= nm_usuario_p;*/
	
	select 	coalesce(max('1'),'3')
	into STRICT	ie_tipo_pessoa_w
	from	Medico b,
		Usuario a
	where 	a.nm_usuario		= nm_usuario_p
	and	a.cd_pessoa_fisica	= b.cd_pessoa_fisica;

	select 	obter_setor_usuario(nm_usuario_p)
	into STRICT	cd_setor_atendimento_w
	;
	
	select	nextval('prescr_medica_seq')
	into STRICT	nr_prescricao_w
	;
	
	ie_forma_adep_w := Obter_Param_Usuario(924, 246, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_forma_adep_w);
		
	if (ie_forma_adep_w = 'DS') then
		select	coalesce(max(ie_adep),'N')
		into STRICT	ie_adep_w
		from	setor_atendimento
		where	cd_setor_atendimento = cd_setor_atendimento_w;
	elsif (ie_forma_adep_w = 'NV') then
		ie_adep_w   := 'N';
	elsif (ie_forma_adep_w = 'PV') then
		ie_adep_w := 'S';
	elsif (ie_forma_adep_w = 'PNV') then
		ie_adep_w := 'N';
	else	
		ie_adep_w := 'S';
	end if;

	insert	into prescr_medica(
		dt_prescricao,
		cd_medico,
		cd_pessoa_fisica,
		cd_estabelecimento,
		nm_usuario_original,
		nr_horas_validade,
		nr_prescricao,
		ie_recem_nato,
		ie_origem_inf,
		dt_primeiro_horario,
		nm_usuario,
		dt_atualizacao,
		cd_prescritor,
		ie_adep,
		nr_atendimento,
		cd_setor_atendimento)
	values (	clock_timestamp(),
		cd_medico_anestesista_w,
		cd_pessoa_fisica_w,
		cd_estabelecimento_p,
		nm_usuario_p,
		24,
		nr_prescricao_w,
		'N',
		ie_tipo_pessoa_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		obter_dados_usuario_opcao(nm_usuario_p, 'C'),
		ie_adep_w,
		nr_atendimento_w,
		cd_setor_atend_prescr_pepo_w);
	end;

end if;

commit;

nr_prescricao_p	:= nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
