SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prescr_material_eme ( nr_prescricao_P bigint, cd_material_p bigint, qt_dose_p bigint, cd_unidade_medida_p text, cd_motivo_baixa_p bigint, nm_usuario_p text, nr_sequencia_p INOUT bigint, dt_horario_p timestamp default null) AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_agrupamento_w		bigint;
nr_intervalo_w			bigint;
nr_horas_validade_w		bigint;
cd_unidade_medida_consumo_w	varchar(50);
cd_intervalo_w			varchar(10);
hr_prim_horario_w		varchar(10);
dt_primeiro_horario_w		timestamp;
ds_horarios_w			varchar(2000);
ds_horarios_ww			varchar(2000);
ie_tipo_material_w		varchar(3);
ie_agrupador_w			smallint;
ie_via_aplicacao_w			varchar(5);
dt_horario_w			prescr_medica.dt_primeiro_horario%type;


BEGIN

dt_horario_w	:= coalesce(dt_horario_p,clock_timestamp());

select	coalesce(max(nr_sequencia),0) + 1,
	coalesce(max(nr_agrupamento),0) + 1
into STRICT	nr_sequencia_w,
	nr_agrupamento_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p;

select	max(cd_unidade_medida_consumo),
	max(ie_tipo_material),
	max(ie_via_aplicacao)
into STRICT cd_unidade_medida_consumo_w,
	 ie_tipo_material_w,
	 ie_via_aplicacao_w
from	material
where	cd_material	= cd_material_p;

if (ie_tipo_material_w = '1') then
	ie_agrupador_w := 2;
else
	ie_agrupador_w := 1;
end if;

select 	max(cd_intervalo)
into STRICT	cd_intervalo_w
from 	intervalo_prescricao
where  	ie_agora     = 'S'
and    	obter_se_intervalo(cd_intervalo, 1)  = 'S'
and    	cd_intervalo = Obter_Menor_Intervalo_agora(1,nr_prescricao_p)
and    	ie_situacao  = 'A';

select	max(dt_primeiro_horario),
	max(nr_horas_validade)
into STRICT	dt_primeiro_horario_w,
	nr_horas_validade_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

select	obter_primeiro_horario(cd_intervalo_w, nr_prescricao_p,cd_material_p,null)
into STRICT	hr_prim_horario_w
;

nr_intervalo_w	:= 0;
						
SELECT * FROM calcular_horario_prescricao(nr_prescricao_p, cd_intervalo_w, dt_primeiro_horario_w, to_date(to_char(dt_primeiro_horario_w,'dd/mm/yyyy') || ' ' || hr_prim_horario_w,'dd/mm/yyyy hh24:mi'), nr_horas_validade_w, cd_material_p, 0, 0, nr_intervalo_w, ds_horarios_w, ds_horarios_ww, 'N', null) INTO STRICT nr_intervalo_w, ds_horarios_w, ds_horarios_ww;
ds_horarios_w := ds_horarios_w || ds_horarios_ww;

if (coalesce(ds_horarios_w::text, '') = '') then
	ds_horarios_w := to_char(dt_horario_w,'hh24:mi');
end if;

				
insert into prescr_material(
	nr_prescricao,
	nr_sequencia,
	ie_origem_inf,
	cd_material,
	cd_unidade_medida,
	qt_dose,
	qt_unitaria,
	qt_material,
	dt_atualizacao,
	nm_usuario,
	cd_intervalo,
	nr_agrupamento,
	ie_urgencia,
	nr_ocorrencia,
	qt_total_dispensar,
	ie_suspenso,
	cd_unidade_medida_dose,
	ie_se_necessario,
	ie_medicacao_paciente,
	cd_motivo_baixa,
	ie_agrupador,
	ie_bomba_infusao,
	ie_aplic_bolus,
	ie_aplic_lenta,
	ie_acm,
	ds_horarios,
	ie_recons_diluente_fixo,
	ie_sem_aprazamento,
	qt_dose_especial,
	hr_dose_especial,
	IE_COBRA_PACIENTE,
	ie_via_aplicacao)
Values (	nr_prescricao_p,
	nr_sequencia_w,
	'S',
	cd_material_p,
	cd_unidade_medida_p,
	qt_dose_p,
	1,
	1,
	clock_timestamp(),
	nm_usuario_p,
	cd_intervalo_w,
	nr_agrupamento_w,
	'S',
	1,
	1,
	'N',
	cd_unidade_medida_consumo_w,
	'N',
	'N',
	cd_motivo_baixa_p,
	ie_agrupador_w,
	'N',
	'N',
	'N',
	'N',
	ds_horarios_w,
	'N',
	'N',
	null,
	null,
	null,
	ie_via_aplicacao_w);
	
CALL Gerar_prescr_mat_sem_dt_lib(nr_prescricao_p,nr_sequencia_w,obter_perfil_ativo,'N',nm_usuario_p,'N','M');
	
nr_sequencia_p	:= nr_sequencia_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_prescr_material_eme ( nr_prescricao_P bigint, cd_material_p bigint, qt_dose_p bigint, cd_unidade_medida_p text, cd_motivo_baixa_p bigint, nm_usuario_p text, nr_sequencia_p INOUT bigint, dt_horario_p timestamp default null) FROM PUBLIC;

