SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prescr_mat_esp_cir (nr_cirurgia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_prescricao_p INOUT bigint) AS $body$
DECLARE

 
ie_forma_adep_w		varchar(255);
cd_medico_cirurgiao_w	varchar(10);
cd_pessoa_fisica_w	varchar(10);
ie_tipo_pessoa_w	smallint;
nr_atendimento_w	bigint;
nr_prescricao_w		bigint;
ie_adep_w		varchar(10);
cd_setor_atendimento_w	bigint;
cd_setor_atendimento_ww	integer;


BEGIN 
 
select 	max(nr_prescricao) 
into STRICT	nr_prescricao_w 
from 	prescr_medica 
where	nr_cirurgia = nr_cirurgia_p 
and 	coalesce(ie_tipo_prescr_cirur,0) <> 2 
and 	coalesce(dt_liberacao::text, '') = '';
 
if (coalesce(nr_prescricao_w::text, '') = '') then 
	ie_forma_adep_w := Obter_Param_Usuario(924, 246, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_forma_adep_w);
	cd_setor_atendimento_ww := obter_param_usuario(900, 548, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, cd_setor_atendimento_ww);
				 
	select	max(cd_medico_cirurgiao), 
		max(cd_pessoa_fisica) 
	into STRICT	cd_medico_cirurgiao_w, 
		cd_pessoa_fisica_w 
	from 	cirurgia 
	where	nr_cirurgia = nr_cirurgia_p;
				 
	if (ie_forma_adep_w = 'DS') then 
		select	coalesce(max(ie_adep),'N') 
		into STRICT	ie_adep_w 
		from	setor_atendimento 
		where	cd_setor_atendimento = cd_setor_atendimento_w;
	elsif (ie_forma_adep_w = 'NV') then 
		ie_adep_w  := 'N';
	elsif (ie_forma_adep_w = 'PV') then 
		ie_adep_w := 'S';
	elsif (ie_forma_adep_w = 'PNV') then 
		ie_adep_w := 'N';
	else 
		ie_adep_w := 'S';
	end if;
 
	select	coalesce(max(b.ie_tipo_pessoa),'1') 
	into STRICT	ie_tipo_pessoa_w 
	from	pessoa_fisica b, 
		usuario a 
	where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
	and	a.nm_usuario		= nm_usuario_p;
 
	select	nextval('prescr_medica_seq') 
	into STRICT	nr_prescricao_w 
	;
 
	insert	into prescr_medica( 
		dt_prescricao, 
		cd_medico, 
		cd_pessoa_fisica, 
		cd_estabelecimento, 
		nm_usuario_original, 
		nr_horas_validade, 
		nr_prescricao, 
		nr_cirurgia, 
		ie_recem_nato, 
		ie_origem_inf, 
		dt_primeiro_horario, 
		nm_usuario, 
		dt_atualizacao, 
		cd_prescritor, 
		ie_tipo_prescr_cirur, 
		nr_atendimento, 
		ie_adep, 
		cd_setor_atendimento) 
	values ( 
		clock_timestamp(), 
		cd_medico_cirurgiao_w, 
		cd_pessoa_fisica_w, 
		cd_estabelecimento_p, 
		nm_usuario_p, 
		24, 
		nr_prescricao_w, 
		nr_cirurgia_p, 
		'N', 
		ie_tipo_pessoa_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		obter_dados_usuario_opcao(nm_usuario_p, 'C'), 
		1, 
		nr_atendimento_w, 
		ie_adep_w, 
		cd_setor_atendimento_ww);
	commit;
end if;
 
nr_prescricao_p := nr_prescricao_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
