CREATE OR REPLACE PROCEDURE gerar_prescr_mat_hor_compl ( nr_seq_mat_hor_p bigint, nm_usuario_p text, ie_susp_duplicidade_p text) AS $body$
DECLARE


nr_seq_mat_compl_w		prescr_mat_hor.nr_seq_mat_compl%type;


BEGIN

select	max(nr_seq_mat_compl)
into STRICT	nr_seq_mat_compl_w
from	prescr_mat_hor
where	nr_sequencia = nr_seq_mat_hor_p;

if (nr_seq_mat_compl_w IS NOT NULL AND nr_seq_mat_compl_w::text <> '') then

	update	prescr_mat_hor_compl
	set		dt_atualizacao	= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			ie_susp_duplicidade	= ie_susp_duplicidade_p
	where	nr_sequencia	= nr_seq_mat_compl_w;

else

	$if dbms_db_version.version >= 11 $then
		nr_seq_mat_compl_w	:= nextval('prescr_mat_hor_compl_seq');
	$else
		select	nextval('prescr_mat_hor_compl_seq')
		into STRICT	nr_seq_mat_compl_w
		;
	$end

	insert into prescr_mat_hor_compl(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ie_susp_duplicidade	)
	values (
		nr_seq_mat_compl_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ie_susp_duplicidade_p);					

	update	prescr_mat_hor
	set		nr_seq_mat_compl = nr_seq_mat_compl_w
	where	nr_sequencia	= nr_seq_mat_hor_p;

end if;	

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
