CREATE OR REPLACE PROCEDURE gerar_prescr_medica_compl ( nr_prescricao_p bigint, nm_usuario_p usuario.nm_usuario%type, nm_usuario_enf_p usuario.nm_usuario%type, dt_analise_enf_p timestamp, si_copy_routine_p text default null) AS $body$
DECLARE


qt_prescr_w 			bigint := 0;


BEGIN

select	count(*)
into STRICT	qt_prescr_w
from	PRESCR_MEDICA_COMPL
where	NR_PRESCRICAO = nr_prescricao_p;

if (qt_prescr_w	=  0)	then
	insert into prescr_medica_compl(
						nr_prescricao,
						dt_atualizacao, 
						nm_usuario, 
						dt_atualizacao_nrec, 
						nm_usuario_nrec, 
						nm_usuario_analise_enf,
						dt_inicio_analise_enf,
						si_copy_routine)
					values (
						nr_prescricao_p,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						nm_usuario_enf_p,
						dt_analise_enf_p,
						si_copy_routine_p);
else
	
	update	prescr_medica_compl
	set		nm_usuario_analise_enf	= coalesce(nm_usuario_enf_p,nm_usuario_analise_enf),
			dt_inicio_analise_enf	= coalesce(dt_analise_enf_p,dt_inicio_analise_enf),
			si_copy_routine = si_copy_routine_p
	where	nr_prescricao = nr_prescricao_p;
	
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
