CREATE OR REPLACE PROCEDURE gerar_prescr_patol_pepo_html ( nr_cirurgia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_prescricao_p INOUT bigint) AS $body$
DECLARE

 
nr_prescricao_w			bigint;
ie_origem_inf_w			varchar(1);
ie_adep_w		  	varchar(10);
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
nr_sequencia_w			bigint;
cd_cgc_w			varchar(14);
cd_setor_atendimento_w		integer;
nr_seq_proc_interno_w		bigint;

BEGIN
 
 
if (coalesce(nr_cirurgia_p,0) > 0) then 
	select	max(cd_setor_atendimento) 
	into STRICT	cd_setor_atendimento_w 
	from 	usuario 
	where 	nm_usuario = nm_usuario_p;
	 
	select	coalesce(max(a.nr_prescricao),0) 
	into STRICT	nr_prescricao_w 
	from	prescr_medica a 
	where	a.nr_cirurgia_patologia = nr_cirurgia_p;
	 
	select 	coalesce(max('1'),'3') 
	into STRICT	ie_origem_inf_w 
	from	Medico b, 
		Usuario a 
	where 	a.nm_usuario		= nm_usuario_p 
	and	a.cd_pessoa_fisica	= b.cd_pessoa_fisica;
 
	if (nr_prescricao_w = 0) then 
		begin 
		Select 	nextval('prescr_medica_seq')  
		into STRICT 	nr_prescricao_w 
		;
 
		select	coalesce(max(ie_adep),'N') 
		into STRICT	ie_adep_w 
		from	setor_atendimento 
		where	cd_setor_atendimento = cd_setor_atendimento_w;
			 
		insert into prescr_medica( 
			nr_prescricao,    
			cd_medico, 
			cd_pessoa_fisica,    
			nr_atendimento,    
			dt_prescricao,    
			dt_atualizacao,    
			nm_usuario, 
			nm_usuario_original,    
			dt_primeiro_horario,    
			cd_setor_atendimento, 
			cd_setor_entrega, 
			dt_entrega, 
			ie_origem_inf, 
			ie_recem_nato, 
			cd_estabelecimento, 
			cd_prescritor, 
			ie_adep, 
			nr_cirurgia_patologia 
			) 
		SELECT 	nr_prescricao_w, 
			cd_medico_cirurgiao, 
			a.cd_pessoa_fisica, 
			a.nr_atendimento, 
			clock_timestamp(), 
			clock_timestamp(), 
			nm_usuario_p, 
			nm_usuario_p,		 
			clock_timestamp(), 
			cd_setor_atendimento_w, 
			cd_setor_atendimento_w, 
			null, 
			ie_origem_inf_w, 
			'N', 
			cd_estabelecimento_p, 
			obter_dados_usuario_opcao(nm_usuario_p, 'C'), 
			ie_adep_w, 
			nr_cirurgia_p 
		from 	cirurgia a 
		where 	nr_cirurgia = nr_cirurgia_p;
		 
		end;
	end if;
end if;	
nr_seq_prescricao_p := nr_prescricao_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
