CREATE OR REPLACE PROCEDURE gerar_prescr_proced_cirurgia ( nr_prescricao_p bigint, nr_seq_proc_paciente_p bigint, nr_seq_agenda_p bigint, ie_tipo_p text, ie_lado_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
dt_prescricao_w		timestamp;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;	
nr_seq_proc_interno_w	bigint;
qt_procedimento_w	double precision := 1;


BEGIN 
 
if (ie_tipo_p = 1) then 
	select	cd_procedimento, 
		ie_origem_proced, 
		nr_seq_proc_interno 
	into STRICT	cd_procedimento_w, 
		ie_origem_proced_w, 
		nr_seq_proc_interno_w	 
	from	procedimento_paciente 
	where	nr_sequencia	= nr_seq_proc_paciente_p;
elsif (ie_tipo_p = 2)	 then 
	select	cd_procedimento, 
		ie_origem_proced, 
		nr_seq_proc_interno 
	into STRICT	cd_procedimento_w, 
		ie_origem_proced_w, 
		nr_seq_proc_interno_w	 
	from	agenda_paciente 
	where	nr_sequencia	= nr_seq_proc_paciente_p;
elsif (ie_tipo_p = 3)	 then	 
	select	cd_procedimento, 
		ie_origem_proced, 
		nr_seq_proc_interno, 
		coalesce(qt_procedimento, 1) 
	into STRICT	cd_procedimento_w, 
		ie_origem_proced_w, 
		nr_seq_proc_interno_w, 
		qt_procedimento_w 
	from	agenda_paciente_proc 
	where	nr_sequencia	= nr_seq_proc_paciente_p 
	and	nr_seq_agenda	= nr_seq_agenda_p;
end if;	
 
select	dt_prescricao 
into STRICT 	dt_prescricao_w 
from 	prescr_medica 
where 	nr_prescricao = nr_prescricao_p;
 
select	coalesce(max(nr_sequencia), 0) + 1 
into STRICT	nr_sequencia_w 
from	prescr_procedimento 
where	nr_prescricao	= nr_prescricao_p;
 
insert into prescr_procedimento( 
	nr_prescricao, 
	nr_sequencia, 
	cd_procedimento, 
	ie_origem_proced, 
	qt_procedimento, 
	ie_urgencia, 
	ie_suspenso, 
	dt_prev_execucao, 
	dt_atualizacao, 
	nm_usuario, 
	ie_origem_inf, 
	nr_seq_interno, 
	ie_avisar_result, 
	cd_motivo_baixa, 
	cd_medico_exec, 
	nr_seq_proc_interno, 
	ie_descricao_cirurgica, 
	ie_lado) 
values (nr_prescricao_p, 
	nr_sequencia_w, 
	cd_procedimento_w, 
	ie_origem_proced_w, 
	qt_procedimento_w, 
	'N', 
	'N', 
	dt_prescricao_w, 
	clock_timestamp(), 
	nm_usuario_p, 
	'1', 
	nextval('prescr_procedimento_seq'), 
	'N', 
	0, 
	Obter_Pessoa_Fisica_Usuario(nm_usuario_p,'C'), 
	nr_seq_proc_interno_w, 
	'S', 
	ie_lado_p);
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
