SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prescr_proced_hemoc_js ( ds_lista_derivados_p text, nr_seq_solic_sangue_p bigint, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_lista_derivados_w	varchar(4000);
ds_lista_aux_w		varchar(4000);
nr_pos_virgula_w	bigint;
nr_pos_hifem_w		bigint;
nr_seq_derivado_w	bigint;
nr_ctrl_loop_w		smallint := 0;
ie_irradiado_w		varchar(10);
ie_filtrado_w		varchar(10);
ds_justificativa_w	varchar(2000);
qt_peso_w			prescr_solic_bco_sangue.qt_peso%type;

BEGIN
if (ds_lista_derivados_p IS NOT NULL AND ds_lista_derivados_p::text <> '') and (nr_seq_solic_sangue_p IS NOT NULL AND nr_seq_solic_sangue_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ds_lista_derivados_w	:= ds_lista_derivados_p;
	while(ds_lista_derivados_w IS NOT NULL AND ds_lista_derivados_w::text <> '')  and (nr_ctrl_loop_w < 100) loop
		begin		
		
		nr_ctrl_loop_w 		:= nr_ctrl_loop_w + 1;
		nr_pos_virgula_w	:= position(';' in ds_lista_derivados_w);
		if (coalesce(nr_pos_virgula_w,0) > 0) then
			begin
			ds_lista_aux_w		:= substr(ds_lista_derivados_w,0,nr_pos_virgula_w-1);
			ds_lista_derivados_w	:= substr(ds_lista_derivados_w,nr_pos_virgula_w+1,length(ds_lista_derivados_w));
			end;
		else
			begin
			ds_lista_aux_w		:= ds_lista_derivados_w;
			ds_lista_derivados_w	:= null;
			end;
		end if;		
		
		if (ds_lista_aux_w IS NOT NULL AND ds_lista_aux_w::text <> '') then
			begin
			nr_pos_hifem_w	:= position('-' in ds_lista_aux_w);
			if (coalesce(nr_pos_hifem_w,0) > 0) then
				begin
				nr_seq_derivado_w	:= substr(ds_lista_aux_w,0,nr_pos_hifem_w-1);
				ds_lista_aux_w		:= substr(ds_lista_aux_w,nr_pos_hifem_w+1,length(ds_lista_aux_w));

				if (ds_lista_aux_w IS NOT NULL AND ds_lista_aux_w::text <> '') then
					begin
					nr_pos_hifem_w	:= position('-' in ds_lista_aux_w);
		
					if (coalesce(nr_pos_hifem_w,0) > 0) then
						begin
						ie_irradiado_w	:= substr(ds_lista_aux_w,1,nr_pos_hifem_w-1);
						ds_lista_aux_w	:= substr(ds_lista_aux_w,nr_pos_hifem_w+1,length(ds_lista_aux_w));
						end;
					end if;
					
					if (ds_lista_aux_w IS NOT NULL AND ds_lista_aux_w::text <> '') then
						begin
						nr_pos_hifem_w	:= position('-' in ds_lista_derivados_w);
		
						if (coalesce(nr_pos_hifem_w,0) > 0) then
							begin
							ie_filtrado_w		:= substr(ds_lista_aux_w,1,nr_pos_hifem_w-1);
							ds_justificativa_w	:= substr(ds_lista_aux_w,nr_pos_hifem_w+1,length(ds_lista_aux_w));
							end;
						else
							begin
							ds_lista_aux_w	:= null;
							end;
						end if;
						end;
					end if;
					end;
				end if;
				end;
			else
				begin
				ds_lista_aux_w	:= null;
				end;
			end if;
			end;
		end if;
		
		if (ds_justificativa_w = 'null') then
			ds_justificativa_w := null;
		end if;

		select	coalesce(max(a.qt_peso),0)
		into STRICT	qt_peso_w
		from	prescr_solic_bco_sangue a,
				prescr_procedimento b
		where	a.nr_sequencia = b.nr_seq_solic_sangue
		and		b.nr_seq_solic_sangue = nr_seq_solic_sangue_p;

		select	max(ie_filtrado),
				max(ds_observacao)
		into STRICT	ie_filtrado_w,
				ds_justificativa_w
		from	rep_definicao_atrib_hem
		where	nr_seq_derivado = nr_seq_derivado_w
		and		((coalesce(qt_peso_max::text, '') = '') or (qt_peso_max >= qt_peso_w));

		CALL gerar_prescr_proced_hemoc(
			nr_seq_derivado_w,
			nr_seq_solic_sangue_p,
			nr_prescricao_p,
			ie_irradiado_w,
			ie_filtrado_w,
			ds_justificativa_w,
			nm_usuario_p);
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_prescr_proced_hemoc_js ( ds_lista_derivados_p text, nr_seq_solic_sangue_p bigint, nr_prescricao_p bigint, nm_usuario_p text) FROM PUBLIC;

