SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_prescr_proc_compl ( nr_prescricao_p bigint, nr_sequencia_p bigint, nm_usuario_p text, ie_laudo_liberado_int_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;


BEGIN

select	max(nr_seq_proc_compl)
into STRICT	nr_sequencia_w
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p
and		nr_sequencia	= nr_sequencia_p;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then

	update	prescr_procedimento_compl
	set		ie_laudo_liberado_int	= ie_laudo_liberado_int_p
	where	nr_sequencia	= nr_sequencia_w;

else

	select	nextval('prescr_procedimento_compl_seq')
	into STRICT	nr_sequencia_w
	;

	insert into prescr_procedimento_compl(	nr_sequencia,
											dt_atualizacao,
											nm_usuario,
											dt_atualizacao_nrec,
											nm_usuario_nrec,
											ie_laudo_liberado_int) values (	nr_sequencia_w,
														clock_timestamp(),
														nm_usuario_p,
														clock_timestamp(),
														nm_usuario_p,
														ie_laudo_liberado_int_p);

	update	prescr_procedimento
	set		nr_seq_proc_compl = nr_sequencia_w
	where	nr_prescricao	= nr_prescricao_p
	and		nr_sequencia	= nr_sequencia_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
